   462â†’        }
   463â†’
   464â†’        const data = await response.json();
   465â†’        analysisResults = data;
   466â†’
   467â†’        // Display results
   468â†’        displayMultitrackResults(data);
   469â†’
   470â†’        // Enable apply button
   471â†’        if (multitrackUI.applyBtn) {
   472â†’            multitrackUI.applyBtn.disabled = false;
   473â†’        }
   474â†’
   475â†’        const errorInfo = data.finalError ? ` (${data.finalError.toFixed(1)}% deviation)` : '';
   476â†’        setStatus(`Auto-balance complete${errorInfo}`);
   477â†’
   478â†’    } catch (err) {
