   480â†’        setStatus('Auto-balance failed: ' + err.message, true);
   481â†’    } finally {
   482â†’        isMultitrackOperationInProgress = false;
   483â†’        if (multitrackUI.autoBalanceBtn) {
   484â†’            multitrackUI.autoBalanceBtn.disabled = false;
   485â†’            multitrackUI.autoBalanceBtn.textContent = 'Auto-Balance';
   486â†’        }
   487â†’    }
   488â†’}
   489â†’
   490â†’// ============================================================================
   491â†’// DISPLAY RESULTS
   492â†’// ============================================================================
   493â†’function displayMultitrackResults(results) {
   494â†’    if (!results) return;
   495â†’
   496â†’    // Show preview section
   497â†’    if (multitrackUI.preview) {
   498â†’        multitrackUI.preview.classList.remove('hidden');
   499â†’    }
   500â†’
   501â†’    // Render distribution chart
   502â†’    renderDistributionChart(results.metadata);
   503â†’
   504â†’    // Render speaker stats
   505â†’    renderSpeakerStats(results.metadata);
   506â†’
   507â†’    // Render decision list
   508â†’    renderDecisionList(results.decisions || []);
   509â†’}
   510â†’
   511â†’function renderDistributionChart(metadata) {
   512â†’    if (!multitrackUI.distributionChart || !metadata) return;
   513â†’
   514â†’    const speakerPercentages = metadata.speakerPercentages || {};
   515â†’    const wideShotPct = metadata.wideShotPercentage || 0;
   516â†’
   517â†’    const fragment = document.createDocumentFragment();
   518â†’
   519â†’    // Add speaker bars
   520â†’    let speakerIndex = 0;
   521â†’    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   522â†’        if (pct > 0) {
   523â†’            const bar = document.createElement('div');
   524â†’            bar.className = `distribution-bar speaker-${speakerIndex % 4}`;
   525â†’            bar.style.width = `${pct}%`;
   526â†’            bar.textContent = pct >= 10 ? `${speaker}: ${pct.toFixed(0)}%` : '';
   527â†’            bar.title = `${speaker}: ${pct.toFixed(1)}%`;
   528â†’            fragment.appendChild(bar);
   529â†’        }
   530â†’        speakerIndex++;
   531â†’    }
   532â†’
   533â†’    // Add wide shot bar
   534â†’    if (wideShotPct > 0) {
   535â†’        const bar = document.createElement('div');
   536â†’        bar.className = 'distribution-bar wide-shot';
   537â†’        bar.style.width = `${wideShotPct}%`;
   538â†’        bar.textContent = wideShotPct >= 8 ? `Wide: ${wideShotPct.toFixed(0)}%` : '';
   539â†’        bar.title = `Wide Shot: ${wideShotPct.toFixed(1)}%`;
   540â†’        fragment.appendChild(bar);
   541â†’    }
   542â†’
   543â†’    multitrackUI.distributionChart.innerHTML = '';
   544â†’    multitrackUI.distributionChart.appendChild(fragment);
   545â†’}
   546â†’
   547â†’function renderSpeakerStats(metadata) {
   548â†’    if (!multitrackUI.speakerStats || !metadata) return;
   549â†’
   550â†’    const speakerPercentages = metadata.speakerPercentages || {};
   551â†’    const wideShotPct = metadata.wideShotPercentage || 0;
   552â†’
   553â†’    const fragment = document.createDocumentFragment();
   554â†’
   555â†’    // Add speaker stats
   556â†’    // SECURITY: Escape speaker names to prevent XSS
   557â†’    let speakerIndex = 0;
   558â†’    for (const [speaker, pct] of Object.entries(speakerPercentages)) {
   559â†’        const stat = document.createElement('div');
   560â†’        stat.className = 'speaker-stat';
   561â†’        stat.innerHTML = `
   562â†’            <span class="speaker-stat-dot speaker-${speakerIndex % 4}"></span>
