   572â†’        stat.className = 'speaker-stat';
   573â†’        stat.innerHTML = `
   574â†’            <span class="speaker-stat-dot wide-shot"></span>
   575â†’            <span>Wide: ${escapeHtml(wideShotPct.toFixed(1))}%</span>
   576â†’        `;
   577â†’        fragment.appendChild(stat);
   578â†’    }
   579â†’
   580â†’    // Add avg shot duration
   581â†’    if (metadata.averageShotDuration) {
   582â†’        const stat = document.createElement('div');
   583â†’        stat.className = 'speaker-stat';
   584â†’        stat.innerHTML = `<span style="color: #888;">Avg: ${escapeHtml(metadata.averageShotDuration.toFixed(1))}s</span>`;
   585â†’        fragment.appendChild(stat);
   586â†’    }
   587â†’
   588â†’    multitrackUI.speakerStats.innerHTML = '';
   589â†’    multitrackUI.speakerStats.appendChild(fragment);
   590â†’}
   591â†’
   592â†’function renderDecisionList(decisions) {
   593â†’    if (!multitrackUI.decisionList) return;
   594â†’
   595â†’    // Update count
   596â†’    if (multitrackUI.decisionCount) {
   597â†’        multitrackUI.decisionCount.textContent = decisions.length;
   598â†’    }
   599â†’
   600â†’    const fragment = document.createDocumentFragment();
   601â†’
   602â†’    // Limit to first 50 for performance
   603â†’    const displayDecisions = decisions.slice(0, 50);
   604â†’
   605â†’    displayDecisions.forEach((decision, i) => {
   606â†’        const item = document.createElement('div');
   607â†’        item.className = 'decision-item';
   608â†’
   609â†’        const speakerClass = decision.isWideShot ? 'wide-shot' : '';
   610â†’        const startTime = formatTime(decision.startTime);
   611â†’        const endTime = formatTime(decision.endTime);
   612â†’
   613â†’        // SECURITY: Escape dynamic content to prevent XSS
   614â†’        item.innerHTML = `
   615â†’            <span class="decision-time">${escapeHtml(startTime)}-${escapeHtml(endTime)}</span>
   616â†’            <span class="decision-speaker ${speakerClass}">${escapeHtml(decision.speakerName)}</span>
   617â†’            <span class="decision-reason">${escapeHtml(decision.reason || '')}</span>
   618â†’            <button class="decision-seek" data-time="${escapeHtml(decision.startTime)}" title="Seek to ${escapeHtml(startTime)}" aria-label="Seek to ${escapeHtml(startTime)}">&gt;</button>
   619â†’        `;
   620â†’
   621â†’        fragment.appendChild(item);
   622â†’    });
   623â†’
   624â†’    if (decisions.length > 50) {
   625â†’        const more = document.createElement('div');
   626â†’        more.className = 'decision-item';
   627â†’        more.style.justifyContent = 'center';
   628â†’        more.style.color = '#888';
   629â†’        more.textContent = `+ ${decisions.length - 50} more decisions`;
   630â†’        fragment.appendChild(more);
   631â†’    }
   632â†’
   633â†’    multitrackUI.decisionList.innerHTML = '';
   634â†’    multitrackUI.decisionList.appendChild(fragment);
   635â†’}
   636â†’
   637â†’function handleDecisionListClick(e) {
   638â†’    const seekBtn = e.target.closest('.decision-seek');
   639â†’    if (seekBtn) {
   640â†’        e.stopPropagation();
   641â†’        const time = parseFloat(seekBtn.dataset.time);
   642â†’        if (!isNaN(time)) {
   643â†’            seekToTime(time);
   644â†’        }
   645â†’    }
   646â†’}
   647â†’
   648â†’// ============================================================================
   649â†’// SEEK TO TIME (CEP VERSION)
   650â†’// ============================================================================
   651â†’async function seekToTime(seconds) {
   652â†’    try {
   653â†’        await jsx.call('seekToTime', seconds);
   654â†’    } catch (e) {
   655â†’        console.warn('[SPLICE Multitrack] Failed to seek:', e);
   656â†’    }
   657â†’}
   658â†’
   659â†’// ============================================================================
   660â†’// APPLY MULTITRACK CUTS
   661â†’// ============================================================================
   662â†’async function applyMultitrackCuts() {
   663â†’    if (!analysisResults || !analysisResults.decisions) {
   664â†’        setStatus('No analysis data. Run analysis first.');
   665â†’        return;
   666â†’    }
   667â†’
   668â†’    if (isMultitrackOperationInProgress) return;
   669â†’
   670â†’    isMultitrackOperationInProgress = true;
   671â†’    if (multitrackUI.applyBtn) {
   672â†’        multitrackUI.applyBtn.disabled = true;
   673â†’        multitrackUI.applyBtn.textContent = 'Applying...';
   674â†’    }
   675â†’
   676â†’    try {
   677â†’        setStatus('Building multitrack sequence...');
   678â†’
   679â†’        // Check if builder is available
   680â†’        if (!window.spliceBuilder) {
   681â†’            throw new Error('Builder not initialized');
   682â†’        }
   683â†’
   684â†’        // Get active sequence info
   685â†’        const seqInfo = await jsx.call('getActiveSequence');
   686â†’        if (!seqInfo) {
   687â†’            throw new Error('No active sequence');
   688â†’        }
   689â†’
   690â†’        // Create cut list from decisions
   691â†’        const cutList = {
   692â†’            version: '3.5',
   693â†’            source: {
   694â†’                name: seqInfo.name,
   695â†’                path: seqInfo.treePath || seqInfo.name
   696â†’            },
   697â†’            segments: analysisResults.decisions.map(d => ({
   698â†’                type: d.isWideShot ? 'wide_shot' : (d.speakerIndex >= 0 ? 'speaker' : 'speech'),
   699â†’                sourceName: seqInfo.name,
   700â†’                sourcePath: seqInfo.treePath || seqInfo.name,
   701â†’                inPoint: d.startTime,
   702â†’                outPoint: d.endTime,
   703â†’                speaker: d.speakerName,
   704â†’                videoTrack: d.videoTrack,
   705â†’                colorHint: d.isWideShot ? 'yellow' :
   706â†’                           (d.speakerIndex === 0 ? 'mango' :
   707â†’                            d.speakerIndex === 1 ? 'caribbean' :
   708â†’                            d.speakerIndex === 2 ? 'lavender' : 'cerulean')
   709â†’            })),
   710â†’            metadata: {
   711â†’                isMultitrack: true,
   712â†’                speakerCount: speakerConfig.length,
   713â†’                decisionCount: analysisResults.decisions.length,
   714â†’                ...analysisResults.metadata
   715â†’            }
   716â†’        };
   717â†’
   718â†’        // Build sequence
   719â†’        const result = await window.spliceBuilder.buildSequenceFromCutList(cutList, {
   720â†’            suffix: '_MULTITRACK'
   721â†’        });
   722â†’
   723â†’        if (result.success) {
   724â†’            // Hide preview
   725â†’            if (multitrackUI.preview) {
   726â†’                multitrackUI.preview.classList.add('hidden');
   727â†’            }
   728â†’
   729â†’            setStatus(`Multitrack sequence created: ${result.stats?.clipsInserted || 0} clips`);
   730â†’
   731â†’            // Clear results
   732â†’            analysisResults = null;
   733â†’        } else {
   734â†’            throw new Error(result.error || 'Failed to build sequence');
   735â†’        }
   736â†’
   737â†’    } catch (err) {
   738â†’        console.error('[SPLICE Multitrack] Apply error:', err);
   739â†’        setStatus('Apply failed: ' + err.message, true);
   740â†’    } finally {
   741â†’        isMultitrackOperationInProgress = false;
   742â†’        if (multitrackUI.applyBtn) {
   743â†’            multitrackUI.applyBtn.disabled = true;
   744â†’            multitrackUI.applyBtn.textContent = 'Apply';
   745â†’        }
   746â†’    }
   747â†’}
   748â†’
   749â†’// ============================================================================
   750â†’// EXPORTS
   751â†’// ============================================================================
   752â†’// Expose for initialization from main.js
   753â†’if (typeof window !== 'undefined') {
   754â†’    window.initMultitrackUI = initMultitrackUI;
   755â†’    window.spliceMultitrack = {
   756â†’        init: initMultitrackUI,
   757â†’        analyze: analyzeMultitrack,
   758â†’        autoBalance: autoBalanceMultitrack,
   759â†’        apply: applyMultitrackCuts,
