   149â†’/**
   150â†’ * Show a confirmation modal dialog (UXP-compatible replacement for confirm())
   151â†’ * @param {string} title - Modal title
   152â†’ * @param {string} message - Confirmation message
   153â†’ * @param {Function} onConfirm - Callback when confirmed
   154â†’ * @param {Function} [onCancel] - Optional callback when cancelled
   155â†’ * @param {Object} [options] - Optional customization { confirmText, cancelText, confirmStyle }
   156â†’ */
   157â†’function showConfirmModal(title, message, onConfirm, onCancel = null, options = {}) {
   158â†’  if (!ui.confirmModal) return;
   159â†’
   160â†’  // Set content
   161â†’  if (ui.confirmModalTitle) ui.confirmModalTitle.textContent = title;
   162â†’  if (ui.confirmModalMessage) ui.confirmModalMessage.textContent = message;
   163â†’
   164â†’  // Customize button text if provided
   165â†’  if (ui.confirmModalConfirmBtn) {
   166â†’    ui.confirmModalConfirmBtn.textContent = options.confirmText || 'Confirm';
   167â†’    ui.confirmModalConfirmBtn.style.background = options.confirmStyle || '#dc3545';
   168â†’  }
   169â†’  if (ui.confirmModalCancelBtn) {
   170â†’    ui.confirmModalCancelBtn.textContent = options.cancelText || 'Cancel';
   171â†’  }
   172â†’
   173â†’  // Store callbacks
   174â†’  confirmModalCallback = { onConfirm, onCancel };
   175â†’
   176â†’  // Show modal
   177â†’  ui.confirmModal.classList.remove('hidden');
   178â†’
   179â†’  // Focus cancel button for safety (user must explicitly confirm)
   180â†’  if (ui.confirmModalCancelBtn) ui.confirmModalCancelBtn.focus();
   181â†’}
   182â†’
   183â†’function hideConfirmModal(confirmed = false) {
   184â†’  if (!ui.confirmModal) return;
   185â†’
   186â†’  ui.confirmModal.classList.add('hidden');
   187â†’
   188â†’  // Execute callback
   189â†’  if (confirmModalCallback) {
   190â†’    if (confirmed && confirmModalCallback.onConfirm) {
   191â†’      confirmModalCallback.onConfirm();
   192â†’    } else if (!confirmed && confirmModalCallback.onCancel) {
   193â†’      confirmModalCallback.onCancel();
   194â†’    }
   195â†’    confirmModalCallback = null;
   196â†’  }
   197â†’}
