   244â†’// ============================================================================
   245â†’// CLEANUP INFRASTRUCTURE (Memory Leak Prevention)
   246â†’// ============================================================================
   247â†’const cleanupRegistry = {
   248â†’  listeners: [],
   249â†’  intervals: [],
   250â†’  canvases: [],
   251â†’
   252â†’  addListener(element, event, handler, options) {
   253â†’    if (!element) return;
   254â†’    element.addEventListener(event, handler, options);
   255â†’    this.listeners.push({ element, event, handler, options });
   256â†’  },
   257â†’
   258â†’  addInterval(id) {
   259â†’    this.intervals.push(id);
   260â†’  },
   261â†’
   262â†’  addCanvas(canvas) {
   263â†’    this.canvases.push(canvas);
   264â†’  },
   265â†’
   266â†’  cleanup() {
   267â†’    // Remove all event listeners
   268â†’    this.listeners.forEach(({ element, event, handler, options }) => {
   269â†’      try {
   270â†’        element.removeEventListener(event, handler, options);
   271â†’      } catch (e) {
   272â†’        console.warn('[SPLICE] Cleanup listener error:', e);
   273â†’      }
   274â†’    });
   275â†’
   276â†’    // Clear all intervals
   277â†’    this.intervals.forEach(id => clearInterval(id));
   278â†’
   279â†’    // Clear canvas contexts
   280â†’    this.canvases.forEach(canvas => {
   281â†’      try {
   282â†’        const ctx = canvas.getContext('2d');
   283â†’        if (ctx) ctx.clearRect(0, 0, canvas.width, canvas.height);
   284â†’      } catch (e) { }
   285â†’    });
   286â†’
   287â†’    // Clear arrays
   288â†’    this.listeners = [];
   289â†’    this.intervals = [];
   290â†’    this.canvases = [];
   291â†’
   292â†’    console.log('[SPLICE] Cleanup complete');
   293â†’  }
   294â†’};
   295â†’
   296â†’// Detect panel close/unload
   297â†’window.addEventListener('beforeunload', () => {
   298â†’  cleanupRegistry.cleanup();
   299â†’  // Clear global state
   300â†’  if (window.splicePreviewState) {
   301â†’    window.splicePreviewState = null;
   302â†’  }
   303â†’});
