   447→/**
   448→ * Set status message
   449→ */
   450→function setStatus(message, isError = false) {
   451→    const status = document.getElementById('status');
   452→    if (status) {
   453→        status.textContent = message;
   454→        status.style.color = isError ? '#dc3545' : '#888';
   455→    }
   456→}
