   762→
   763→  // Apply preset settings
   764→  const newSettings = {
   765→    ...preset.settings,
   766→    activePreset: presetName
   767→  };
   768→
   769→  saveSettings(newSettings);
   770→  console.log(`[SPLICE] Applied preset: ${preset.name}`);
