  1253→      return null;
  1254→    }
  1255→
  1256→    const contents = await file.read({ format: uxpFs.formats.binary });
  1257→    return contents;
  1258→  } catch (err) {
  1259→    console.error(`[SPLICE] Error reading file ${filename}:`, err);
  1260→    return null;
  1261→  }
  1262→}
