  1342→
  1343→      try {
  1344→        // Call license activation API (uses fetchWithTimeout from config.js)
  1345→        const response = await fetchWithTimeout(`${getBackendUrl()}/license/activate`, {
  1346→          method: 'POST',
  1347→          headers: { 'Content-Type': 'application/json' },
  1348→          body: JSON.stringify({ key: licenseKey })
  1349→        }, FETCH_TIMEOUT_DEFAULT);
  1350→
  1351→        const result = await response.json();
  1352→
  1353→        if (!result.success) {
  1354→          showLoginError(result.error || 'Activation failed');
  1355→          return;
