  1368→
  1369→        if (typeof setStatus === 'function') {
  1370→          setStatus(`License activated! ${result.tierName} tier - ${result.hoursRemaining.toFixed(1)}h remaining`);
  1371→        }
  1372→      } catch (err) {
  1373→        console.error('[SPLICE] License activation error:', err);
  1374→
  1375→        // DIAGNOSTIC LOGGING: Capture exact error details for debugging
  1376→        console.log('[SPLICE DEBUG] === LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
  1377→        console.log('[SPLICE DEBUG] Error name:', err?.name);
  1378→        console.log('[SPLICE DEBUG] Error message:', err?.message);
  1379→        console.log('[SPLICE DEBUG] Error stack:', err?.stack);
  1380→        console.log('[SPLICE DEBUG] Backend URL:', getBackendUrl());
  1381→        console.log('[SPLICE DEBUG] Fetch timeout:', FETCH_TIMEOUT_DEFAULT);
  1382→        console.log('[SPLICE DEBUG] Is UXP environment:', typeof uxp !== 'undefined');
  1383→        console.log('[SPLICE DEBUG] User agent:', navigator?.userAgent);
  1384→        console.log('[SPLICE DEBUG] ===============================================');
  1385→
  1386→        // Provide detailed error message based on error type
  1387→        let errorMessage = 'Connection error. ';
  1388→
  1389→        if (err.message === 'Request timed out') {
  1390→          errorMessage += 'Server is not responding. Check if backend is running.';
  1391→        } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
  1392→          errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
  1393→        } else if (err.message?.includes('CORS')) {
  1394→          errorMessage += 'CORS error. Contact support.';
  1395→        } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
  1396→          errorMessage += 'SSL certificate error. Try updating your system certificates.';
  1397→        } else if (err.message) {
  1398→          errorMessage += err.message;
  1399→        } else {
  1400→          errorMessage += 'Unknown error. Check console for details.';
  1401→        }
  1402→
  1403→        showLoginError(errorMessage);
  1404→      } finally {
  1405→        saveLoginBtn.disabled = false;
  1406→        saveLoginBtn.textContent = 'Activate';
  1407→      }
  1408→    });
  1409→  }
  1410→
  1411→  // Email-based license lookup
  1412→  const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  1413→  const lookupEmailInput = document.getElementById('lookupEmailInput');
  1414→  const lookupResult = document.getElementById('lookupResult');
  1415→  const foundLicenseKey = document.getElementById('foundLicenseKey');
  1416→
  1417→  if (lookupLicenseBtn) {
  1418→    lookupLicenseBtn.addEventListener('click', async () => {
  1419→      const email = lookupEmailInput?.value?.trim()?.toLowerCase();
  1420→
  1421→      if (!email) {
  1422→        showLoginError('Please enter your email address');
  1423→        return;
  1424→      }
  1425→
  1426→      // Basic email validation
  1427→      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1428→      if (!emailRegex.test(email)) {
  1429→        showLoginError('Please enter a valid email address');
  1430→        return;
  1431→      }
  1432→
  1433→      // Hide previous results/errors
  1434→      if (loginError) loginError.style.display = 'none';
  1435→      if (lookupResult) lookupResult.style.display = 'none';
  1436→
  1437→      // Show loading state
  1438→      lookupLicenseBtn.disabled = true;
  1439→      lookupLicenseBtn.textContent = 'Looking up...';
  1440→
  1441→      try {
  1442→        const response = await fetchWithTimeout(`${getBackendUrl()}/license/lookup`, {
  1443→          method: 'POST',
  1444→          headers: { 'Content-Type': 'application/json' },
  1445→          body: JSON.stringify({ email })
  1446→        }, 30000);
  1447→
  1448→        const result = await response.json();
  1449→
  1450→        if (result.success && result.key) {
  1451→          // Show the found license key
  1452→          if (foundLicenseKey) foundLicenseKey.textContent = result.key;
  1453→          if (lookupResult) lookupResult.style.display = 'block';
  1454→          // Auto-fill the license key input
  1455→          if (licenseKeyInput) licenseKeyInput.value = result.key;
  1456→        } else {
