  1541→
  1542→// =============================================================================
  1543→// PROFANITY SETTINGS (Feature 5)
  1544→// =============================================================================
  1545→
  1546→const PROFANITY_SETTINGS_KEY = 'spliceProfanitySettings';
  1547→
  1548→/**
  1549→ * Default profanity settings
  1550→ */
  1551→const DEFAULT_PROFANITY_SETTINGS = {
  1552→  enabled: false,
  1553→  language: 'en',
  1554→  customBlocklist: [],
  1555→  customAllowlist: [],
  1556→  bleepType: 'standard'
  1557→};
  1558→
  1559→/**
  1560→ * Get profanity settings from localStorage
  1561→ * @returns {Object} Profanity settings
  1562→ */
  1563→function getProfanitySettings() {
  1564→  try {
  1565→    const saved = localStorage.getItem(PROFANITY_SETTINGS_KEY);
  1566→    if (saved) {
  1567→      const parsed = JSON.parse(saved);
  1568→      return {
  1569→        ...DEFAULT_PROFANITY_SETTINGS,
  1570→        ...parsed
  1571→      };
  1572→    }
  1573→  } catch (e) {
  1574→    console.warn('[SPLICE] Could not load profanity settings:', e);
  1575→  }
  1576→  return { ...DEFAULT_PROFANITY_SETTINGS };
  1577→}
  1578→
  1579→/**
  1580→ * Save profanity settings to localStorage
  1581→ * @param {Object} settings - Profanity settings to save
  1582→ * @returns {boolean} Success status
  1583→ */
  1584→function saveProfanitySettings(settings) {
  1585→  try {
  1586→    const toSave = {
  1587→      ...getProfanitySettings(),
  1588→      ...settings
  1589→    };
  1590→    localStorage.setItem(PROFANITY_SETTINGS_KEY, JSON.stringify(toSave));
  1591→    return true;
  1592→  } catch (e) {
  1593→    console.warn('[SPLICE] Could not save profanity settings:', e);
  1594→    return false;
  1595→  }
  1596→}
  1597→
  1598→/**
  1599→ * Parse comma-separated word list into array
  1600→ * @param {string} text - Comma or newline separated words
  1601→ * @returns {Array<string>} Array of trimmed words
  1602→ */
  1603→function parseWordList(text) {
  1604→  if (!text || typeof text !== 'string') return [];
  1605→  return text
  1606→    .split(/[,\n]/)
  1607→    .map(word => word.trim().toLowerCase())
  1608→    .filter(word => word.length > 0);
  1609→}
  1610→
  1611→/**
  1612→ * Format array of words as comma-separated string
  1613→ * @param {Array<string>} words - Array of words
  1614→ * @returns {string} Comma-separated string
  1615→ */
  1616→function formatWordList(words) {
  1617→  if (!Array.isArray(words)) return '';
  1618→  return words.join(', ');
  1619→}
  1620→
  1621→/**
  1622→ * Initialize profanity settings UI
  1623→ * Wires up UI elements to settings
  1624→ */
  1625→function initProfanitySettingsUI() {
  1626→  const enableCheckbox = document.getElementById('enableProfanity');
  1627→  const settingsPanel = document.getElementById('profanitySettings');
  1628→  const languageSelect = document.getElementById('profanityLanguage');
  1629→  const blocklistTextarea = document.getElementById('profanityBlocklist');
  1630→  const allowlistTextarea = document.getElementById('profanityAllowlist');
  1631→  const bleepTypeSelect = document.getElementById('bleepType');
  1632→
  1633→  if (!enableCheckbox || !settingsPanel) {
  1634→    console.log('[SPLICE] Profanity UI elements not found');
  1635→    return;
  1636→  }
  1637→
  1638→  // Load saved settings
  1639→  const saved = getProfanitySettings();
  1640→
  1641→  // Apply saved values to UI
  1642→  enableCheckbox.checked = saved.enabled;
  1643→  if (saved.enabled) {
  1644→    settingsPanel.classList.remove('collapsed');
  1645→  }
  1646→
  1647→  if (languageSelect) languageSelect.value = saved.language || 'en';
  1648→  if (blocklistTextarea) blocklistTextarea.value = formatWordList(saved.customBlocklist);
  1649→  if (allowlistTextarea) allowlistTextarea.value = formatWordList(saved.customAllowlist);
  1650→  if (bleepTypeSelect) bleepTypeSelect.value = saved.bleepType || 'standard';
  1651→
  1652→  // Toggle settings panel visibility
  1653→  enableCheckbox.addEventListener('change', () => {
  1654→    if (enableCheckbox.checked) {
  1655→      settingsPanel.classList.remove('collapsed');
  1656→    } else {
  1657→      settingsPanel.classList.add('collapsed');
  1658→    }
  1659→    saveProfanitySettings({ enabled: enableCheckbox.checked });
  1660→  });
  1661→
  1662→  // Save on change
  1663→  if (languageSelect) {
  1664→    languageSelect.addEventListener('change', () => {
  1665→      saveProfanitySettings({ language: languageSelect.value });
  1666→    });
  1667→  }
  1668→
  1669→  if (blocklistTextarea) {
  1670→    blocklistTextarea.addEventListener('blur', () => {
  1671→      const words = parseWordList(blocklistTextarea.value);
  1672→      saveProfanitySettings({ customBlocklist: words });
  1673→      // Re-format to clean up input
  1674→      blocklistTextarea.value = formatWordList(words);
  1675→    });
  1676→  }
  1677→
  1678→  if (allowlistTextarea) {
  1679→    allowlistTextarea.addEventListener('blur', () => {
  1680→      const words = parseWordList(allowlistTextarea.value);
  1681→      saveProfanitySettings({ customAllowlist: words });
  1682→      // Re-format to clean up input
