   532→
   533→async function runDetection() {
   534→    if (isOperationInProgress) return;
   535→
   536→    // Check sequence
   537→    const hasSequence = await checkSequenceOpen();
   538→    if (!hasSequence) {
   539→        setStatus('Please open a sequence first', true);
   540→        return;
   541→    }
   542→
   543→    // Check online
   544→    if (!isOnline()) {
   545→        setStatus('Offline - Check your connection', true);
   546→        return;
   547→    }
   548→
   549→    isOperationInProgress = true;
   550→    ui.goBtn.disabled = true;
   551→    showProgress('Detecting silences...');
   552→
   553→    try {
   554→        const settings = getSettings();
   555→
   556→        // Get sequence info
   557→        const seqInfo = await jsx.call('getActiveSequence');
   558→        console.log('[SPLICE] Sequence info:', seqInfo);
   559→
   560→        // Store sequence info for later use
   561→        currentSequenceInfo = seqInfo;
   562→        window.spliceState.activeSequence = seqInfo;
   563→        if (seqInfo && seqInfo.videoPath) {
   564→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   565→        }
   566→
   567→        // Export audio via JSX for silence detection
   568→        updateProgress(10, 'Exporting audio...');
   569→
   570→        let audioFilePath = null;
   571→        try {
   572→            // Get sequence info for audio export
   573→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   574→            if (exportResult && exportResult.success && exportResult.outputPath) {
   575→                audioFilePath = exportResult.outputPath;
   576→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   577→            } else if (exportResult && exportResult.error) {
   578→                throw new Error(exportResult.error);
   579→            }
   580→        } catch (exportError) {
   581→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   582→            // Try to get audio from first clip in timeline
   583→            try {
   584→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   585→                if (clipInfo && clipInfo.path) {
   586→                    audioFilePath = clipInfo.path;
   587→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   588→                }
   589→            } catch (clipError) {
   590→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   591→            }
   592→        }
   593→
   594→        if (!audioFilePath) {
   595→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   596→        }
   597→
   598→        // Store audio path globally for other modules
   599→        currentAudioPath = audioFilePath;
   600→        window.spliceState.audioPath = audioFilePath;
   601→
   602→        // Call backend for silence detection with audio file path
   603→        updateProgress(30, 'Detecting silences...');
   604→        const silenceResponse = await fetchWithTimeout(
   605→            `${getBackendUrl()}/silences-rms`,
   606→            {
   607→                method: 'POST',
   608→                headers: getAuthHeaders(),
   609→                body: JSON.stringify({
   610→                    wavPath: audioFilePath,
   611→                    sensitivity: settings.sensitivity,
   612→                    minSilenceLength: settings.minSilenceLength || 0.5
   613→                })
   614→            }
   615→        );
   616→
   617→        if (!silenceResponse.ok) {
   618→            throw new Error(await parseErrorResponse(silenceResponse));
   619→        }
   620→
   621→        const silenceData = await silenceResponse.json();
   622→        previewSilences = silenceData.silences || [];
   623→
   624→        // Detect takes if enabled
   625→        if (settings.enableTakesDetection) {
   626→            updateProgress(60, 'Detecting takes...');
   627→            try {
   628→                const analyzeResponse = await fetchWithTimeout(
   629→                    `${getBackendUrl()}/analyze`,
   630→                    {
   631→                        method: 'POST',
   632→                        headers: getAuthHeaders(),
   633→                        body: JSON.stringify({
   634→                            wavPath: audioFilePath,
   635→                            detectTakes: true
   636→                        })
   637→                    }
   638→                );
   639→
   640→                if (analyzeResponse.ok) {
   641→                    const analyzeData = await analyzeResponse.json();
   642→                    previewTakes = analyzeData.takes || [];
   643→
   644→                    // Store transcript for captions/music modules
   645→                    if (analyzeData.transcript) {
   646→                        currentTranscript = analyzeData.transcript;
   647→                        window.currentTranscript = analyzeData.transcript;
   648→                        window.spliceState.lastTranscript = analyzeData.transcript;
   649→                    }
   650→                } else {
   651→                    // Handle specific error codes
   652→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   653→                    if (analyzeResponse.status === 402) {
   654→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   655→                        setStatus('Takes detection skipped: insufficient credits', true);
   656→                    } else if (analyzeResponse.status === 401) {
   657→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   658→                    } else {
   659→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   660→                    }
   661→                }
   662→            } catch (e) {
   663→                console.warn('[SPLICE] Takes detection failed:', e.message);
   664→            }
   665→        }
   666→
   667→        // Detect profanity if enabled
   668→        if (settings.enableProfanity) {
   669→            updateProgress(65, 'Detecting profanity...');
   670→            try {
   671→                const profanityResponse = await fetchWithTimeout(
   672→                    `${getBackendUrl()}/profanity`,
   673→                    {
   674→                        method: 'POST',
   675→                        headers: getAuthHeaders(),
   676→                        body: JSON.stringify({
   677→                            wavPath: audioFilePath,
   678→                            language: settings.profanityLanguage || 'en',
   679→                            bleepType: settings.bleepType || 'standard'
   680→                        })
   681→                    }
   682→                );
   683→
   684→                if (profanityResponse.ok) {
   685→                    const profanityData = await profanityResponse.json();
   686→                    currentProfanity = profanityData.instances || [];
   687→                    if (currentProfanity.length > 0) {
   688→                        console.log(`[SPLICE] Found ${currentProfanity.length} profanity instances`);
   689→                    }
   690→                } else {
   691→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   692→                }
   693→            } catch (e) {
   694→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   695→            }
   696→        }
   697→
   698→        // Detect chapters if enabled
   699→        if (settings.enableChapters) {
   700→            updateProgress(80, 'Detecting chapters...');
   701→            try {
   702→                const chapterResponse = await fetchWithTimeout(
   703→                    `${getBackendUrl()}/chapters`,
   704→                    {
   705→                        method: 'POST',
   706→                        headers: getAuthHeaders(),
   707→                        body: JSON.stringify({
   708→                            wavPath: audioFilePath,
   709→                            transcript: currentTranscript,
   710→                            maxChapters: settings.maxChapters || 10,
   711→                            minChapterLength: settings.minChapterLength || 60
   712→                        })
   713→                    }
   714→                );
   715→
   716→                if (chapterResponse.ok) {
   717→                    const chapterData = await chapterResponse.json();
   718→                    currentChapters = chapterData.chapters || [];
   719→                }
   720→            } catch (e) {
   721→                console.warn('[SPLICE] Chapter detection failed:', e);
   722→            }
   723→        }
   724→
   725→        // Detect zoom points if enabled
   726→        if (settings.enableZoom) {
   727→            updateProgress(90, 'Detecting zoom points...');
   728→            try {
   729→                const zoomResponse = await fetchWithTimeout(
   730→                    `${getBackendUrl()}/zoom`,
   731→                    {
   732→                        method: 'POST',
   733→                        headers: getAuthHeaders(),
   734→                        body: JSON.stringify({
   735→                            wavPath: audioFilePath,
   736→                            transcript: currentTranscript,
   737→                            frequency: settings.zoomFrequency || 'medium',
   738→                            preset: settings.zoomPreset || 'medium',
   739→                            placement: settings.zoomPlacement || 'sentence_start'
   740→                        })
   741→                    }
   742→                );
   743→
   744→                if (zoomResponse.ok) {
   745→                    const zoomData = await zoomResponse.json();
   746→                    currentZooms = zoomData.zoomPoints || [];
   747→                }
   748→            } catch (e) {
   749→                console.warn('[SPLICE] Zoom detection failed:', e);
   750→            }
   751→        }
   752→
   753→        // Store results
   754→        lastDetectionResult = {
   755→            silences: previewSilences,
   756→            takes: previewTakes,
   757→            chapters: currentChapters,
   758→            zooms: currentZooms
   759→        };
   760→
   761→        // Show preview
   762→        hideProgress();
   763→        showCombinedPreview();
   764→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   765→
   766→    } catch (error) {
   767→        console.error('[SPLICE] Detection error:', error);
   768→        setStatus('Detection failed: ' + error.message, true);
   769→        hideProgress();
   770→    } finally {
   771→        isOperationInProgress = false;
   772→        ui.goBtn.disabled = false;
   773→    }
   774→}
