   828→function renderPreviewList() {
   829→    if (!ui.previewList) return;
   830→
   831→    const html = [];
   832→
   833→    // Render silences
   834→    previewSilences.forEach((silence, i) => {
   835→        const isSelected = selectedSilenceIndices.has(i);
   836→        const duration = silence.end - silence.start;
   837→        html.push(`
   838→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   839→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   840→                <div class="preview-item-info">
   841→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   842→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   843→                </div>
   844→            </div>
   845→        `);
   846→    });
   847→
   848→    // Render takes
   849→    previewTakes.forEach((take, i) => {
   850→        // SECURITY: Escape take label to prevent XSS
   851→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
   852→        html.push(`
   853→            <div class="preview-item take-item" data-index="${i}" data-type="take">
   854→                <div class="preview-item-icon">🎬</div>
   855→                <div class="preview-item-info">
   856→                    <div class="preview-item-time">${formatTime(take.start)} - ${formatTime(take.end)}</div>
   857→                    <div class="preview-item-label">${safeLabel}</div>
   858→                </div>
   859→            </div>
   860→        `);
   861→    });
   862→
   863→    ui.previewList.innerHTML = html.join('');
   864→
   865→    // Add click handlers
   866→    ui.previewList.querySelectorAll('.preview-item').forEach(item => {
   867→        item.addEventListener('click', (e) => {
   868→            const index = parseInt(item.dataset.index);
   869→            const type = item.dataset.type;
   870→
   871→            if (e.target.classList.contains('preview-item-check')) {
   872→                // Checkbox clicked
   873→                toggleSilenceSelection(index);
   874→            } else {
   875→                // Item clicked - seek to time
   876→                const data = type === 'silence' ? previewSilences[index] : previewTakes[index];
   877→                seekToTime(data.start);
   878→            }
   879→        });
   880→    });
   881→}
   882→
   883→function toggleSilenceSelection(index) {
   884→    if (selectedSilenceIndices.has(index)) {
   885→        selectedSilenceIndices.delete(index);
   886→    } else {
   887→        selectedSilenceIndices.add(index);
   888→    }
   889→    updateSelectedCount();
   890→    renderPreviewList();
   891→}
   892→
   893→function updateSelectedCount() {
   894→    if (ui.selectedCount) {
   895→        ui.selectedCount.textContent = selectedSilenceIndices.size;
   896→    }
   897→    if (ui.selectAllSilences) {
   898→        ui.selectAllSilences.checked = selectedSilenceIndices.size === previewSilences.length;
   899→    }
   900→}
   901→
   902→async function seekToTime(seconds) {
   903→    try {
   904→        // Check if there's an active sequence before seeking
   905→        if (!app?.project?.activeSequence) {
   906→            console.warn('[SPLICE] No active sequence to seek in');
   907→            return;
   908→        }
   909→        // Use JSX to set playhead position
   910→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
   911→        const ticks = Math.round(seconds * 254016000000);
   912→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
   913→    } catch (e) {
   914→        console.warn('[SPLICE] Seek failed:', e);
   915→    }
   916→}
