   929→                <div class="chapter-time">${formatTime(chapter.startTime)}</div>
   930→                <div class="chapter-title">${safeTitle}</div>
   931→            </div>
   932→        `;
   933→    }).join('');
   934→
   935→    ui.chapterList.innerHTML = html;
