    13→const https = require('https');
    14→const licenseService = require('../services/licenseService');
    15→
    16→const BASE_URL = 'https://127.0.0.1:3847';
    17→// eslint-disable-next-line no-unused-vars -- kept for reference
    18→const TEST_CUSTOMER_ID = 'cus_test_phase6_license';
    19→
    20→// Allow self-signed certs
    21→const agent = new https.Agent({ rejectUnauthorized: false });
    22→
    23→/**
    24→ * Helper: Make HTTPS request
    25→ */
    26→function request(method, path, body = null, headers = {}) {
    27→  return new Promise((resolve, reject) => {
    28→    const url = new URL(path, BASE_URL);
    29→    const options = {
    30→      method,
    31→      hostname: url.hostname,
    32→      port: url.port,
    33→      path: url.pathname,
    34→      headers: {
    35→        'Content-Type': 'application/json',
    36→        ...headers
    37→      },
    38→      agent
    39→    };
    40→
    41→    const req = https.request(options, (res) => {
    42→      let data = '';
    43→      res.on('data', (chunk) => data += chunk);
    44→      res.on('end', () => {
    45→        try {
    46→          resolve({ status: res.statusCode, data: JSON.parse(data) });
    47→        } catch (_e) {
    48→          resolve({ status: res.statusCode, data: data });
    49→        }
    50→      });
    51→    });
    52→
    53→    req.on('error', reject);
    54→    if (body) req.write(JSON.stringify(body));
    55→    req.end();
    56→  });
    57→}
