    63→function testLicenseKeyFormat() {
    64→  console.log('\n=== Test: License Key Format Validation ===');
    65→  let passed = 0;
    66→  let failed = 0;
    67→
    68→  // Test valid format
    69→  const validKeys = [
    70→    'SPLICE-ABCD-EFGH-JKLM',
    71→    'SPLICE-2345-6789-NPQR',
    72→    'splice-abcd-efgh-jklm', // lowercase should be normalized
    73→    'SPLICE-A2B3-C4D5-E6F7'
    74→  ];
    75→
    76→  for (const key of validKeys) {
    77→    if (licenseService.isValidKeyFormat(key)) {
    78→      console.log(`  ✓ Valid key accepted: ${key}`);
    79→      passed++;
    80→    } else {
    81→      console.log(`  ✗ Valid key rejected: ${key}`);
    82→      failed++;
    83→    }
    84→  }
    85→
    86→  // Test invalid formats
    87→  const invalidKeys = [
    88→    'SPLICE-ABC-DEF-GHI',         // Too short
    89→    'SPLICE-ABCDE-FGHI-JKLM',     // Too long
    90→    'PREFIX-ABCD-EFGH-JKLM',      // Wrong prefix
    91→    'SPLICEABCDEFGHJKLM',         // No dashes
    92→    'SPLICE-ABCD-EFGH',           // Missing segment
    93→    'SPLICE-0000-1111-OOOO',      // Contains O and 0/1 (confusing)
    94→    '',                            // Empty
    95→    null,                          // Null
    96→    123456                         // Number
    97→  ];
    98→
    99→  for (const key of invalidKeys) {
   100→    if (!licenseService.isValidKeyFormat(key)) {
   101→      console.log(`  ✓ Invalid key rejected: ${key}`);
   102→      passed++;
   103→    } else {
   104→      console.log(`  ✗ Invalid key accepted: ${key}`);
   105→      failed++;
   106→    }
   107→  }
   108→
   109→  // Test key generation format
   110→  console.log('\nTesting generated key format...');
   111→  for (let i = 0; i < 10; i++) {
   112→    const generatedKey = licenseService.generateKeyString();
   113→    if (licenseService.isValidKeyFormat(generatedKey)) {
   114→      passed++;
   115→    } else {
   116→      console.log(`  ✗ Generated key invalid: ${generatedKey}`);
   117→      failed++;
   118→    }
   119→  }
   120→  console.log(`  ✓ 10 generated keys have valid format`);
   121→
   122→  // Test key normalization
   123→  console.log('\nTesting key normalization...');
   124→  const testCases = [
   125→    { input: 'splice-abcd-efgh-jklm', expected: 'SPLICE-ABCD-EFGH-JKLM' },
   126→    { input: '  SPLICE-ABCD-EFGH-JKLM  ', expected: 'SPLICE-ABCD-EFGH-JKLM' },
   127→    { input: 'Splice-Abcd-Efgh-Jklm', expected: 'SPLICE-ABCD-EFGH-JKLM' }
   128→  ];
   129→
   130→  for (const tc of testCases) {
   131→    const normalized = licenseService.normalizeKey(tc.input);
   132→    if (normalized === tc.expected) {
   133→      console.log(`  ✓ Normalized correctly: "${tc.input}" → "${normalized}"`);
   134→      passed++;
   135→    } else {
   136→      console.log(`  ✗ Normalization failed: expected "${tc.expected}", got "${normalized}"`);
   137→      failed++;
   138→    }
   139→  }
   140→
   141→  console.log(`\nFormat validation: ${passed} passed, ${failed} failed`);
   142→  return { passed, failed };
   143→}
