   149→function testLicenseKeyGeneration() {
   150→  console.log('\n=== Test: License Key Generation (Unit) ===');
   151→  let passed = 0;
   152→  let failed = 0;
   153→
   154→  // Test key uniqueness
   155→  console.log('Testing key uniqueness...');
   156→  const generatedKeys = new Set();
   157→  const iterations = 100;
   158→
   159→  for (let i = 0; i < iterations; i++) {
   160→    const key = licenseService.generateKeyString();
   161→    generatedKeys.add(key);
   162→  }
   163→
   164→  if (generatedKeys.size === iterations) {
   165→    console.log(`  ✓ All ${iterations} generated keys are unique`);
   166→    passed++;
   167→  } else {
   168→    console.log(`  ✗ Only ${generatedKeys.size}/${iterations} keys unique`);
   169→    failed++;
   170→  }
   171→
   172→  // Test key character set (only in the random segments, not the prefix)
   173→  console.log('Testing key character set...');
   174→  const randomCharsOnly = 'ABCDEFGHJKMNPQRSTUVWXYZ23456789';
   175→  let allCharsValid = true;
   176→
   177→  for (const key of generatedKeys) {
   178→    // Extract random segments (skip "SPLICE-" prefix and dashes)
   179→    const segments = key.split('-').slice(1); // Skip "SPLICE"
   180→    for (const segment of segments) {
   181→      for (const char of segment) {
   182→        if (!randomCharsOnly.includes(char)) {
   183→          console.log(`  ✗ Invalid character found: ${char} in segment ${segment}`);
   184→          allCharsValid = false;
   185→          break;
   186→        }
   187→      }
   188→    }
   189→  }
   190→
   191→  if (allCharsValid) {
   192→    console.log(`  ✓ All characters in allowed set`);
   193→    passed++;
   194→  } else {
   195→    failed++;
   196→  }
   197→
   198→  // Test key length
   199→  console.log('Testing key length...');
   200→  let allCorrectLength = true;
   201→  for (const key of generatedKeys) {
   202→    if (key.length !== 21) { // SPLICE-XXXX-XXXX-XXXX = 6+1+4+1+4+1+4 = 21
   203→      console.log(`  ✗ Incorrect length: ${key} (${key.length})`);
   204→      allCorrectLength = false;
   205→    }
   206→  }
   207→
   208→  if (allCorrectLength) {
   209→    console.log(`  ✓ All keys have correct length (21 chars)`);
   210→    passed++;
   211→  } else {
   212→    failed++;
   213→  }
   214→
   215→  console.log(`\nKey generation: ${passed} passed, ${failed} failed`);
   216→  return { passed, failed };
   217→}
