   450→// =============================================================================
   451→// Test: UI Validation Pattern (Unit Tests)
   452→// =============================================================================
   453→
   454→function testUIValidationPattern() {
   455→  console.log('\n=== Test: UI Validation Pattern ===');
   456→  let passed = 0;
   457→  let failed = 0;
   458→
   459→  // Simulate the frontend validation regex
   460→  const uiPattern = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;
   461→
   462→  const testCases = [
   463→    { key: 'SPLICE-ABCD-EFGH-JKLM', expected: true },
   464→    { key: 'SPLICE-2345-6789-NPQR', expected: true },
   465→    { key: 'SPLICE-A2B3-C4D5-E6F7', expected: true },
   466→    { key: 'splice-abcd-efgh-jklm', expected: false }, // Must be uppercase
   467→    { key: 'SPLICE-ABCD-EFGH', expected: false },       // Too short
   468→    { key: 'SPLICE-ABCDE-FGHI-JKLM', expected: false }, // Segment too long
   469→    { key: 'PREFIX-ABCD-EFGH-JKLM', expected: false },  // Wrong prefix
   470→    { key: 'SPLICE-0000-1111-LLLL', expected: false },  // Contains O, 0, 1, L
   471→  ];
   472→
   473→  for (const tc of testCases) {
   474→    const matches = uiPattern.test(tc.key);
   475→    if (matches === tc.expected) {
   476→      console.log(`  ✓ "${tc.key}" → ${tc.expected}`);
   477→      passed++;
   478→    } else {
   479→      console.log(`  ✗ "${tc.key}" expected ${tc.expected}, got ${matches}`);
   480→      failed++;
   481→    }
   482→  }
   483→
   484→  console.log(`\nUI validation: ${passed} passed, ${failed} failed`);
   485→  return { passed, failed };
   486→}
