   488→// =============================================================================
   489→// Test: Performance (Key Generation Speed)
   490→// =============================================================================
   491→
   492→function testPerformance() {
   493→  console.log('\n=== Test: Performance ===');
   494→  let passed = 0;
   495→  let failed = 0;
   496→
   497→  // Test key generation speed
   498→  console.log('Testing key generation speed...');
   499→  const iterations = 1000;
   500→  const start = process.hrtime.bigint();
   501→
   502→  for (let i = 0; i < iterations; i++) {
   503→    licenseService.generateKeyString();
   504→  }
   505→
   506→  const end = process.hrtime.bigint();
   507→  const durationMs = Number(end - start) / 1e6;
   508→  const avgPerKey = durationMs / iterations;
   509→
   510→  if (avgPerKey < 1) { // Should be less than 1ms per key
   511→    console.log(`  ✓ Generated ${iterations} keys in ${durationMs.toFixed(2)}ms (${avgPerKey.toFixed(4)}ms avg)`);
   512→    passed++;
   513→  } else {
   514→    console.log(`  ✗ Key generation too slow: ${avgPerKey.toFixed(4)}ms avg`);
   515→    failed++;
   516→  }
   517→
   518→  // Test validation speed
   519→  console.log('Testing validation speed...');
   520→  const testKey = 'SPLICE-ABCD-EFGH-JKLM';
   521→  const validationStart = process.hrtime.bigint();
   522→
   523→  for (let i = 0; i < iterations; i++) {
   524→    licenseService.isValidKeyFormat(testKey);
   525→  }
   526→
   527→  const validationEnd = process.hrtime.bigint();
   528→  const validationMs = Number(validationEnd - validationStart) / 1e6;
   529→  const avgValidation = validationMs / iterations;
   530→
   531→  if (avgValidation < 0.1) { // Should be less than 0.1ms per validation
   532→    console.log(`  ✓ Validated ${iterations} keys in ${validationMs.toFixed(2)}ms (${avgValidation.toFixed(4)}ms avg)`);
   533→    passed++;
   534→  } else {
   535→    console.log(`  ✗ Validation too slow: ${avgValidation.toFixed(4)}ms avg`);
   536→    failed++;
   537→  }
   538→
   539→  console.log(`\nPerformance: ${passed} passed, ${failed} failed`);
   540→  return { passed, failed };
   541→}
