/**
 * Media folder entry (cached after selection)
 * @type {Object|null}
 */
let mediaFolderEntry = null;

/**
 * Prompt user to select a media folder and create a persistent token.
 * This allows silent file access without repeated permission prompts.
 *
 * @returns {Promise<{success: boolean, path?: string, error?: string}>}
 */
async function setupMediaFolder() {
  try {
    // Open folder picker dialog
    const folder = await uxpFs.getFolder();

    if (!folder) {
      return { success: false, error: 'No folder selected' };
    }

    // Create a persistent token for this folder
    const token = await uxpFs.createPersistentToken(folder);

    if (!token) {
      return { success: false, error: 'Failed to create persistent token' };
    }

    // Get the folder path for display
    const folderPath = folder.nativePath || folder.name;

    // Save to settings
    saveSettings({
      mediaFolderToken: token,
      mediaFolderPath: folderPath
    });

    // Cache the folder entry
    mediaFolderEntry = folder;

    console.log(`[SPLICE] Media folder set: ${folderPath}`);

    return {
      success: true,
      path: folderPath,
      token
    };
  } catch (err) {
    console.error('[SPLICE] Error setting up media folder:', err);
    return { success: false, error: err.message };
  }
}

/**
 * Get the media folder entry using the stored persistent token.
 * Falls back to prompting if token is invalid.
 *
 * @returns {Promise<Object|null>} The folder entry or null
 */
async function getMediaFolder() {
  // Return cached entry if available
  if (mediaFolderEntry) {
    return mediaFolderEntry;
  }

  const settings = getSettings();

  // Try to restore from persistent token
  if (settings.mediaFolderToken) {
    try {
      const folder = await uxpFs.getEntryForPersistentToken(settings.mediaFolderToken);
      if (folder) {
        mediaFolderEntry = folder;
        console.log(`[SPLICE] Restored media folder from token: ${settings.mediaFolderPath}`);
        return folder;
      }
    } catch (err) {
      console.warn('[SPLICE] Could not restore media folder from token:', err.message);
      // Clear invalid token
      saveSettings({ mediaFolderToken: null, mediaFolderPath: null });
    }
  }

  return null;
}

/**
 * Check if a media folder is configured
 * @returns {boolean}
 */
function hasMediaFolder() {
  const settings = getSettings();
  return !!settings.mediaFolderToken;
}

/**
 * Get the configured media folder path (for display)
 * @returns {string|null}
 */
function getMediaFolderPath() {
  const settings = getSettings();
  return settings.mediaFolderPath;
}

/**
 * Clear the media folder configuration
 */
function clearMediaFolder() {
  mediaFolderEntry = null;
  saveSettings({
    mediaFolderToken: null,
    mediaFolderPath: null
  });
  console.log('[SPLICE] Media folder cleared');
}

/**
 * Read a file from the media folder
 * @param {string} filename - Name of the file to read
 * @returns {Promise<ArrayBuffer|null>} File contents or null
 */
async function readMediaFile(filename) {
  const folder = await getMediaFolder();
  if (!folder) {
    console.warn('[SPLICE] No media folder configured');
    return null;
  }

  try {
    const file = await folder.getEntry(filename);
    if (!file) {
      console.warn(`[SPLICE] File not found in media folder: ${filename}`);
      return null;
    }

    const contents = await file.read({ format: uxpFs.formats.binary });
    return contents;
  } catch (err) {
    console.error(`[SPLICE] Error reading file ${filename}:`, err);
    return null;
  }
}

// =============================================================================
// LOGIN MODAL
// =============================================================================

/**
 * Validate license key format: SPLICE-XXXX-XXXX-XXXX
 * @param {string} key - License key to validate
 * @returns {boolean} True if format is valid
 */
function isValidLicenseKeyFormat(key) {
  if (!key || typeof key !== 'string') return false;
  const normalized = key.toUpperCase().trim();
  const pattern = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;
  return pattern.test(normalized);
}

/**
 * Initialize login modal handlers
 */
function initLoginModal() {
  const loginModal = document.getElementById('loginModal');
  const closeLoginBtn = document.getElementById('closeLoginBtn');
  const saveLoginBtn = document.getElementById('saveLoginBtn');
  const licenseKeyInput = document.getElementById('licenseKeyInput');
  const loginError = document.getElementById('loginError');
  const creditBadge = document.getElementById('creditBadge');

  // Handle credit badge click - retry on error, or show login modal
  if (creditBadge) {
    creditBadge.addEventListener('click', async () => {
      // If in error state, retry fetching credits
      if (creditBadge.classList.contains('error')) {
        creditBadge.textContent = '...';
        if (typeof refreshCredits === 'function') {
          await refreshCredits();
        }
        return;
      }
      // Otherwise show login modal
      showLoginModal();
    });
  }

  // Close login modal
  if (closeLoginBtn && loginModal) {
    closeLoginBtn.addEventListener('click', () => {
      loginModal.classList.add('hidden');
    });
  }

  // Close on backdrop click
  if (loginModal) {
    loginModal.addEventListener('click', (e) => {
      if (e.target === loginModal) {
        loginModal.classList.add('hidden');
      }
    });
  }

  // Activate license key
  if (saveLoginBtn) {
    saveLoginBtn.addEventListener('click', async () => {
      const licenseKey = licenseKeyInput?.value?.trim()?.toUpperCase();

      if (!licenseKey) {
        showLoginError('Please enter your license key');
        return;
      }

      if (!isValidLicenseKeyFormat(licenseKey)) {
        showLoginError('Invalid format. Expected: SPLICE-XXXX-XXXX-XXXX');
        return;
      }

      // Show loading state
      saveLoginBtn.disabled = true;
      saveLoginBtn.textContent = 'Activating...';

      try {
        // Call license activation API (uses fetchWithTimeout from config.js)
        const response = await fetchWithTimeout(`${getBackendUrl()}/license/activate`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ key: licenseKey })
        }, FETCH_TIMEOUT_DEFAULT);

        const result = await response.json();

        if (!result.success) {
          showLoginError(result.error || 'Activation failed');
          return;
        }

        // Save customer ID from activation response
        saveSettings({ customerId: result.customerId });

        // Hide modal
        loginModal?.classList.add('hidden');

        // Refresh credits display
        if (typeof refreshCredits === 'function') {
          await refreshCredits();
        }

        if (typeof setStatus === 'function') {
          setStatus(`License activated! ${result.tierName} tier - ${result.hoursRemaining.toFixed(1)}h remaining`);
        }
      } catch (err) {
        console.error('[SPLICE] License activation error:', err);
        showLoginError('Connection error. Check server is running.');
      } finally {
        saveLoginBtn.disabled = false;
        saveLoginBtn.textContent = 'Activate';
      }
    });
  }

  // Email-based license lookup
  const lookupLicenseBtn = document.getElementById('lookupLicenseBtn');
  const lookupEmailInput = document.getElementById('lookupEmailInput');
  const lookupResult = document.getElementById('lookupResult');
  const foundLicenseKey = document.getElementById('foundLicenseKey');

  if (lookupLicenseBtn) {
    lookupLicenseBtn.addEventListener('click', async () => {
      const email = lookupEmailInput?.value?.trim()?.toLowerCase();

      if (!email) {
        showLoginError('Please enter your email address');
        return;
      }

      // Basic email validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        showLoginError('Please enter a valid email address');
        return;
      }

      // Hide previous results/errors
      if (loginError) loginError.style.display = 'none';
      if (lookupResult) lookupResult.style.display = 'none';

      // Show loading state
      lookupLicenseBtn.disabled = true;
      lookupLicenseBtn.textContent = 'Looking up...';

      try {
        const response = await fetchWithTimeout(`${getBackendUrl()}/license/lookup`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ email })
        }, 30000);

        const result = await response.json();

        if (result.success && result.key) {
          // Show the found license key
          if (foundLicenseKey) foundLicenseKey.textContent = result.key;
          if (lookupResult) lookupResult.style.display = 'block';
          // Auto-fill the license key input
          if (licenseKeyInput) licenseKeyInput.value = result.key;
        } else {
          showLoginError(result.error || result.message || 'No license found for this email');
        }
      } catch (err) {
        console.error('[SPLICE] License lookup error:', err);
        showLoginError('Failed to look up license. Please try again.');
      } finally {
        lookupLicenseBtn.disabled = false;
        lookupLicenseBtn.textContent = 'Find My License';
      }
    });
  }

  // Clear input on modal open (don't show old value for security)
  // Customer ID is stored but license key is not displayed
}

/**
 * Show login modal
 */
function showLoginModal() {
  const loginModal = document.getElementById('loginModal');
  const loginError = document.getElementById('loginError');
  const loginSuccess = document.getElementById('loginSuccess');
  const lookupResult = document.getElementById('lookupResult');

  // Clear previous states
  if (loginError) loginError.style.display = 'none';
  if (loginSuccess) loginSuccess.style.display = 'none';
  if (lookupResult) lookupResult.style.display = 'none';

  if (loginModal) {
    loginModal.classList.remove('hidden');
  }
}

/**
 * Show login error message
 */
function showLoginError(message) {
  const loginError = document.getElementById('loginError');
  if (loginError) {
    loginError.textContent = message;
    loginError.style.display = 'block';
  }
}

/**
 * Check if user is logged in
 * @returns {boolean}
 */
function isLoggedIn() {
  const settings = getSettings();
  return !!settings.customerId;
}

/**
 * Get customer ID
 * @returns {string|null}
 */
function getCustomerId() {
  const settings = getSettings();
  return settings.customerId || null;
}

/**
 * Logout - clear customer ID and credits cache
 */
function logout() {
  saveSettings({ customerId: null });

  // Clear credits cache (defined in credits.js)
  if (typeof clearCreditsCache === 'function') {
    clearCreditsCache();
  }

  // Update credit badge to show login state
  if (typeof updateCreditDisplay === 'function') {
    updateCreditDisplay(null);
  }

  if (typeof setStatus === 'function') {
    setStatus('Logged out');
  }
}

// =============================================================================
// PROFANITY SETTINGS (Feature 5)
// =============================================================================

const PROFANITY_SETTINGS_KEY = 'spliceProfanitySettings';

/**
 * Default profanity settings
 */
const DEFAULT_PROFANITY_SETTINGS = {
  enabled: false,
  language: 'en',
  customBlocklist: [],
  customAllowlist: [],
  bleepType: 'standard'
};

/**
 * Get profanity settings from localStorage
 * @returns {Object} Profanity settings
 */
function getProfanitySettings() {
  try {
    const saved = localStorage.getItem(PROFANITY_SETTINGS_KEY);
    if (saved) {
      const parsed = JSON.parse(saved);
      return {
        ...DEFAULT_PROFANITY_SETTINGS,
        ...parsed
      };
    }
  } catch (e) {
    console.warn('[SPLICE] Could not load profanity settings:', e);
  }
  return { ...DEFAULT_PROFANITY_SETTINGS };
}

/**
 * Save profanity settings to localStorage
 * @param {Object} settings - Profanity settings to save
 * @returns {boolean} Success status
 */
function saveProfanitySettings(settings) {
  try {
    const toSave = {
      ...getProfanitySettings(),
      ...settings
    };
    localStorage.setItem(PROFANITY_SETTINGS_KEY, JSON.stringify(toSave));
    return true;
  } catch (e) {
    console.warn('[SPLICE] Could not save profanity settings:', e);
    return false;
  }
}

/**
 * Parse comma-separated word list into array
 * @param {string} text - Comma or newline separated words
 * @returns {Array<string>} Array of trimmed words
 */
function parseWordList(text) {
  if (!text || typeof text !== 'string') return [];
  return text
    .split(/[,\n]/)
    .map(word => word.trim().toLowerCase())
    .filter(word => word.length > 0);
}

/**
 * Format array of words as comma-separated string
 * @param {Array<string>} words - Array of words
 * @returns {string} Comma-separated string
 */
function formatWordList(words) {
  if (!Array.isArray(words)) return '';
  return words.join(', ');
}

/**
 * Initialize profanity settings UI
 * Wires up UI elements to settings
 */
function initProfanitySettingsUI() {
  const enableCheckbox = document.getElementById('enableProfanity');
  const settingsPanel = document.getElementById('profanitySettings');
  const languageSelect = document.getElementById('profanityLanguage');
  const blocklistTextarea = document.getElementById('profanityBlocklist');
  const allowlistTextarea = document.getElementById('profanityAllowlist');
  const bleepTypeSelect = document.getElementById('bleepType');

  if (!enableCheckbox || !settingsPanel) {
    console.log('[SPLICE] Profanity UI elements not found');
    return;
  }

  // Load saved settings
  const saved = getProfanitySettings();

  // Apply saved values to UI
  enableCheckbox.checked = saved.enabled;
  if (saved.enabled) {
    settingsPanel.classList.remove('collapsed');
  }

  if (languageSelect) languageSelect.value = saved.language || 'en';
  if (blocklistTextarea) blocklistTextarea.value = formatWordList(saved.customBlocklist);
  if (allowlistTextarea) allowlistTextarea.value = formatWordList(saved.customAllowlist);
  if (bleepTypeSelect) bleepTypeSelect.value = saved.bleepType || 'standard';

  // Toggle settings panel visibility
  enableCheckbox.addEventListener('change', () => {
    if (enableCheckbox.checked) {
      settingsPanel.classList.remove('collapsed');
    } else {
      settingsPanel.classList.add('collapsed');
    }
    saveProfanitySettings({ enabled: enableCheckbox.checked });
  });

  // Save on change
  if (languageSelect) {
    languageSelect.addEventListener('change', () => {
      saveProfanitySettings({ language: languageSelect.value });
    });
  }

  if (blocklistTextarea) {
    blocklistTextarea.addEventListener('blur', () => {
      const words = parseWordList(blocklistTextarea.value);
      saveProfanitySettings({ customBlocklist: words });
      // Re-format to clean up input
      blocklistTextarea.value = formatWordList(words);
    });
  }

  if (allowlistTextarea) {
    allowlistTextarea.addEventListener('blur', () => {
      const words = parseWordList(allowlistTextarea.value);
      saveProfanitySettings({ customAllowlist: words });
      // Re-format to clean up input
      allowlistTextarea.value = formatWordList(words);
    });
  }

  if (bleepTypeSelect) {
    bleepTypeSelect.addEventListener('change', () => {
      saveProfanitySettings({ bleepType: bleepTypeSelect.value });
    });
  }

  console.log('[SPLICE] Profanity settings UI initialized');
}

// Load settings on script load
loadSettings();

