/**
 * SPLICE Shared Utilities - Validation
 *
 * Input validation utilities.
 * Used by both UXP and CEP plugins.
 */

/**
 * License key format pattern: SPLICE-XXXX-XXXX-XXXX
 * @type {RegExp}
 */
const LICENSE_KEY_PATTERN = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;

/**
 * Email validation pattern
 * @type {RegExp}
 */
const EMAIL_PATTERN = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

/**
 * YouTube URL validation patterns
 * @type {RegExp[]}
 */
const YOUTUBE_URL_PATTERNS = [
  /youtube\.com\/watch\?v=[\w-]{11}/,
  /youtu\.be\/[\w-]{11}/,
  /youtube\.com\/embed\/[\w-]{11}/
];

/**
 * Validate license key format
 * @param {string} key - License key to validate
 * @returns {boolean} True if format is valid
 */
function isValidLicenseKeyFormat(key) {
  if (!key || typeof key !== 'string') return false;
  const normalized = key.toUpperCase().trim();
  return LICENSE_KEY_PATTERN.test(normalized);
}

/**
 * Normalize license key (uppercase and trim)
 * @param {string} key - License key to normalize
 * @returns {string} Normalized key
 */
function normalizeLicenseKey(key) {
  if (!key || typeof key !== 'string') return '';
  return key.toUpperCase().trim();
}

/**
 * Validate email address format
 * @param {string} email - Email to validate
 * @returns {boolean} True if format is valid
 */
function isValidEmail(email) {
  if (!email || typeof email !== 'string') return false;
  return EMAIL_PATTERN.test(email.trim());
}

/**
 * Normalize email (lowercase and trim)
 * @param {string} email - Email to normalize
 * @returns {string} Normalized email
 */
function normalizeEmail(email) {
  if (!email || typeof email !== 'string') return '';
  return email.toLowerCase().trim();
}

/**
 * Validate YouTube URL
 * @param {string} url - URL to validate
 * @returns {boolean} True if valid YouTube URL
 */
function isValidYouTubeUrl(url) {
  if (!url || typeof url !== 'string') return false;
  return YOUTUBE_URL_PATTERNS.some(pattern => pattern.test(url));
}

/**
 * Extract video ID from YouTube URL
 * @param {string} url - YouTube URL
 * @returns {string|null} Video ID or null
 */
function extractYouTubeVideoId(url) {
  if (!url || typeof url !== 'string') return null;

  // youtube.com/watch?v=XXXXXXXXXXX
  const watchMatch = url.match(/youtube\.com\/watch\?v=([\w-]{11})/);
  if (watchMatch) return watchMatch[1];

  // youtu.be/XXXXXXXXXXX
  const shortMatch = url.match(/youtu\.be\/([\w-]{11})/);
  if (shortMatch) return shortMatch[1];

  // youtube.com/embed/XXXXXXXXXXX
  const embedMatch = url.match(/youtube\.com\/embed\/([\w-]{11})/);
  if (embedMatch) return embedMatch[1];

  return null;
}

/**
 * Validate preset name
 * @param {string} name - Preset name to validate
 * @returns {{valid: boolean, error?: string}} Validation result
 */
function validatePresetName(name) {
  if (!name || typeof name !== 'string') {
    return { valid: false, error: 'Name is required' };
  }

  const trimmed = name.trim();

  if (trimmed.length === 0) {
    return { valid: false, error: 'Name cannot be empty' };
  }

  if (trimmed.length > 50) {
    return { valid: false, error: 'Name must be 50 characters or less' };
  }

  return { valid: true };
}

/**
 * Validate number within range
 * @param {number} value - Value to validate
 * @param {number} min - Minimum value
 * @param {number} max - Maximum value
 * @returns {boolean} True if valid
 */
function isInRange(value, min, max) {
  if (typeof value !== 'number' || isNaN(value)) return false;
  return value >= min && value <= max;
}

/**
 * Validate settings object structure
 * @param {Object} settings - Settings to validate
 * @returns {{valid: boolean, errors: string[]}} Validation result
 */
function validateSettings(settings) {
  const errors = [];

  if (!settings || typeof settings !== 'object') {
    return { valid: false, errors: ['Settings must be an object'] };
  }

  // Validate sensitivity (0-100)
  if (settings.sensitivity !== undefined) {
    if (!isInRange(settings.sensitivity, 0, 100)) {
      errors.push('Sensitivity must be between 0 and 100');
    }
  }

  // Validate threshold (-60 to 0)
  if (settings.threshold !== undefined) {
    if (!isInRange(settings.threshold, -60, 0)) {
      errors.push('Threshold must be between -60 and 0');
    }
  }

  // Validate minSilenceLength (0.1 to 10)
  if (settings.minSilenceLength !== undefined) {
    if (!isInRange(settings.minSilenceLength, 0.1, 10)) {
      errors.push('Minimum silence length must be between 0.1 and 10 seconds');
    }
  }

  // Validate J-Cut values (0 to 2)
  if (settings.jCutLeadIn !== undefined) {
    if (!isInRange(settings.jCutLeadIn, 0, 2)) {
      errors.push('J-Cut lead-in must be between 0 and 2 seconds');
    }
  }

  if (settings.jCutLeadOut !== undefined) {
    if (!isInRange(settings.jCutLeadOut, 0, 2)) {
      errors.push('J-Cut lead-out must be between 0 and 2 seconds');
    }
  }

  return { valid: errors.length === 0, errors };
}

// Export for different module systems
if (typeof module !== 'undefined' && module.exports) {
  module.exports = {
    LICENSE_KEY_PATTERN,
    EMAIL_PATTERN,
    YOUTUBE_URL_PATTERNS,
    isValidLicenseKeyFormat,
    normalizeLicenseKey,
    isValidEmail,
    normalizeEmail,
    isValidYouTubeUrl,
    extractYouTubeVideoId,
    validatePresetName,
    isInRange,
    validateSettings
  };
}

// Export for browser/UXP
if (typeof window !== 'undefined') {
  window.SpliceValidation = {
    LICENSE_KEY_PATTERN,
    EMAIL_PATTERN,
    YOUTUBE_URL_PATTERNS,
    isValidLicenseKeyFormat,
    normalizeLicenseKey,
    isValidEmail,
    normalizeEmail,
    isValidYouTubeUrl,
    extractYouTubeVideoId,
    validatePresetName,
    isInRange,
    validateSettings
