 */
const LICENSE_KEY_PATTERN = /^SPLICE-[A-Z2-9]{4}-[A-Z2-9]{4}-[A-Z2-9]{4}$/;

/**
 * Email validation pattern
 * @type {RegExp}
 */
const EMAIL_PATTERN = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

/**
 * YouTube URL validation patterns
 * @type {RegExp[]}
 */
const YOUTUBE_URL_PATTERNS = [
  /youtube\.com\/watch\?v=[\w-]{11}/,
  /youtu\.be\/[\w-]{11}/,
  /youtube\.com\/embed\/[\w-]{11}/
];

/**
 * Validate license key format
 * @param {string} key - License key to validate
 * @returns {boolean} True if format is valid
 */
function isValidLicenseKeyFormat(key) {
  if (!key || typeof key !== 'string') return false;
  const normalized = key.toUpperCase().trim();
  return LICENSE_KEY_PATTERN.test(normalized);
}

/**
 * Normalize license key (uppercase and trim)
 * @param {string} key - License key to normalize
 * @returns {string} Normalized key
 */
function normalizeLicenseKey(key) {
  if (!key || typeof key !== 'string') return '';
  return key.toUpperCase().trim();
}

/**
 * Validate email address format
 * @param {string} email - Email to validate
 * @returns {boolean} True if format is valid
 */
function isValidEmail(email) {
  if (!email || typeof email !== 'string') return false;
  return EMAIL_PATTERN.test(email.trim());
}

/**
 * Normalize email (lowercase and trim)
 * @param {string} email - Email to normalize
 * @returns {string} Normalized email
 */
function normalizeEmail(email) {
  if (!email || typeof email !== 'string') return '';
  return email.toLowerCase().trim();
}

/**
 * Validate YouTube URL
