   201→// ============================================================================
   202→// FETCH UTILITIES
   203→// ============================================================================
   204→
   205→/**
   206→ * Fetch with timeout support
   207→ * @param {string} url - URL to fetch
   208→ * @param {object} options - Fetch options
   209→ * @param {number} timeout - Timeout in ms
   210→ */
   211→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   212→    // DIAGNOSTIC: Log fetch request details (debug mode only)
   213→    debugFetch('=== FETCH REQUEST START ===');
   214→    debugFetch('URL:', url);
   215→    debugFetch('Method:', options?.method || 'GET');
   216→    debugFetch('Headers:', options?.headers);
   217→    debugFetch('Timeout:', timeout);
   218→    debugFetch('Is CEP environment:', typeof CSInterface !== 'undefined');
   219→    debugFetch('=================================');
   220→
   221→    const controller = new AbortController();
   222→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   223→
   224→    try {
   225→        const response = await fetch(url, {
   226→            ...options,
   227→            signal: controller.signal
   228→        });
   229→        clearTimeout(timeoutId);
   230→
   231→        // DIAGNOSTIC: Log successful response (debug mode only)
   232→        debugFetch('=== FETCH RESPONSE SUCCESS ===');
   233→        debugFetch('Status:', response.status);
   234→        debugFetch('OK:', response.ok);
   235→        debugFetch('=================================');
   236→
   237→        return response;
   238→    } catch (error) {
   239→        clearTimeout(timeoutId);
   240→
   241→        // DIAGNOSTIC: Log raw fetch error (debug mode only)
   242→        debugFetch('=== FETCH ERROR CAUGHT ===');
   243→        debugFetch('Error name:', error?.name);
   244→        debugFetch('Error message:', error?.message);
   245→        debugFetch('Error type:', typeof error);
   246→        debugFetch('Error constructor:', error?.constructor?.name);
   247→        debugFetch('Is AbortError:', error.name === 'AbortError');
   248→        debugFetch('Is TypeError:', error.name === 'TypeError');
   249→        debugFetch('Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
   250→        debugFetch('=================================');
   251→
   252→        if (error.name === 'AbortError') {
   253→            throw new Error(`Request timeout after ${timeout}ms`);
   254→        }
   255→        throw error;
   256→    }
   257→}
   258→
   259→/**
   260→ * Parse error response from backend
   261→ * @param {Response} response - Fetch response
   262→ */
   263→async function parseErrorResponse(response) {
   264→    try {
   265→        const data = await response.json();
   266→        return data.error || data.message || `HTTP ${response.status}`;
   267→    } catch {
   268→        return `HTTP ${response.status}: ${response.statusText}`;
   269→    }
   270→}
