   272→/**
   273→ * Get auth headers for API requests
   274→ * Uses JWT Bearer token if available, with legacy fallback
   275→ */
   276→function getAuthHeaders() {
   277→    const headers = { 'Content-Type': 'application/json' };
   278→    const settings = getSettings();
   279→
   280→    // Prefer JWT Bearer token (modern auth)
   281→    if (settings.accessToken) {
   282→        headers['Authorization'] = `Bearer ${settings.accessToken}`;
   283→        return headers;
   284→    }
   285→
   286→    // Legacy fallback for backwards compatibility
   287→    // This will be removed after 2026-04-11
   288→    if (settings.customerId) {
   289→        headers['x-stripe-customer-id'] = settings.customerId;
   290→        console.warn('[SPLICE] Using deprecated x-stripe-customer-id header. Please re-login to upgrade to JWT auth.');
   291→    }
   292→
   293→    return headers;
   294→}
   295→
   296→/**
   297→ * Check if the access token is expired or about to expire
   298→ * @returns {boolean} True if token needs refresh
   299→ */
   300→function isTokenExpired() {
   301→    const settings = getSettings();
   302→    if (!settings.tokenExpiry) return true;
   303→
   304→    // Refresh if less than 5 minutes remaining
   305→    const bufferMs = 5 * 60 * 1000;
   306→    return Date.now() >= (settings.tokenExpiry - bufferMs);
   307→}
   308→
   309→/**
   310→ * Refresh the access token using the refresh token
   311→ * @returns {Promise<boolean>} True if refresh succeeded
   312→ */
   313→async function refreshAccessToken() {
   314→    const settings = getSettings();
   315→
   316→    if (!settings.refreshToken) {
   317→        debugLog('[Auth] No refresh token available');
   318→        return false;
   319→    }
   320→
   321→    try {
   322→        debugLog('[Auth] Refreshing access token...');
   323→
   324→        const response = await fetchWithTimeout(`${getBackendUrl()}/auth/refresh`, {
   325→            method: 'POST',
   326→            headers: { 'Content-Type': 'application/json' },
   327→            body: JSON.stringify({ refreshToken: settings.refreshToken })
   328→        }, 30000);
   329→
   330→        if (!response.ok) {
   331→            const errorMsg = await parseErrorResponse(response);
   332→            console.error('[Auth] Token refresh failed:', errorMsg);
   333→
   334→            // If refresh token is invalid, clear auth and require re-login
   335→            if (response.status === 401) {
   336→                clearAuthTokens();
   337→            }
   338→            return false;
   339→        }
   340→
   341→        const data = await response.json();
   342→
   343→        // Save new access token
   344→        const expiresInMs = (data.expiresIn || 86400) * 1000; // Default 24h
   345→        saveSettings({
   346→            accessToken: data.token,
   347→            tokenExpiry: Date.now() + expiresInMs
   348→        });
   349→
   350→        debugLog('[Auth] Access token refreshed successfully');
   351→        return true;
   352→    } catch (err) {
   353→        console.error('[Auth] Token refresh error:', err);
   354→        return false;
   355→    }
   356→}
   357→
   358→/**
   359→ * Clear all auth tokens (call on logout or auth failure)
   360→ */
   361→function clearAuthTokens() {
   362→    saveSettings({
   363→        accessToken: null,
   364→        refreshToken: null,
   365→        tokenExpiry: null
   366→    });
   367→    debugLog('[Auth] Auth tokens cleared');
   368→}
   369→
   370→/**
   371→ * Ensure we have a valid access token, refreshing if needed
   372→ * @returns {Promise<boolean>} True if we have a valid token
   373→ */
   374→async function ensureValidToken() {
   375→    const settings = getSettings();
   376→
   377→    // No token at all - need to login
   378→    if (!settings.accessToken) {
   379→        return false;
   380→    }
   381→
   382→    // Token not expired - good to go
   383→    if (!isTokenExpired()) {
   384→        return true;
   385→    }
   386→
   387→    // Try to refresh
   388→    return await refreshAccessToken();
   389→}
