   632→// ============================================================================
   633→// OFFLINE DETECTION
   634→// ============================================================================
   635→
   636→let isOnlineState = true;
   637→let offlineCheckInterval = null;
   638→
   639→/**
   640→ * Check if online
   641→ */
   642→function isOnline() {
   643→    return isOnlineState && navigator.onLine;
   644→}
   645→
   646→/**
   647→ * Initialize offline detection
   648→ */
   649→function initOfflineDetection() {
   650→    // Browser events
   651→    window.addEventListener('online', () => {
   652→        isOnlineState = true;
   653→        updateOfflineUI(true);
   654→    });
   655→
   656→    window.addEventListener('offline', () => {
   657→        isOnlineState = false;
   658→        updateOfflineUI(false);
   659→    });
   660→
   661→    // Periodic check
   662→    offlineCheckInterval = setInterval(async () => {
   663→        try {
   664→            const response = await fetchWithTimeout(
   665→                `${getBackendUrl()}/health`,
   666→                { method: 'GET' },
   667→                5000
   668→            );
   669→            isOnlineState = response.ok;
   670→        } catch {
   671→            isOnlineState = false;
   672→        }
   673→        updateOfflineUI(isOnlineState);
   674→    }, 30000);
   675→}
   676→
   677→/**
   678→ * Update UI based on online state
   679→ */
