  1028→/**
  1029→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
  1030→ * Character set excludes confusing chars: 0/O, 1/I/L
  1031→ */
  1032→function isValidLicenseKeyFormat(key) {
  1033→    if (!key || typeof key !== 'string') return false;
  1034→    const normalized = key.toUpperCase().trim();
  1035→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
  1036→    return pattern.test(normalized);
  1037→}
