  1096→
  1097→            try {
  1098→                // Use /auth/login endpoint which returns JWT tokens
  1099→                const response = await fetchWithTimeout(`${getBackendUrl()}/auth/login`, {
  1100→                    method: 'POST',
  1101→                    headers: { 'Content-Type': 'application/json' },
  1102→                    body: JSON.stringify({ licenseKey: licenseKey })
  1103→                }, SPLICE_CONFIG.FETCH_TIMEOUT);
  1104→
  1105→                const result = await response.json();
  1106→
  1107→                if (!result.success) {
