  1109→                    return;
  1110→                }
  1111→
  1112→                // Store JWT tokens and customer info
  1113→                const expiresInMs = (result.expiresIn || 86400) * 1000; // Default 24h
  1114→                saveSettings({
  1115→                    customerId: result.customerId,
  1116→                    accessToken: result.token,
  1117→                    refreshToken: result.refreshToken,
  1118→                    tokenExpiry: Date.now() + expiresInMs,
  1119→                    licenseKey: licenseKey // Keep for re-authentication
  1120→                });
  1121→
  1122→                loginModal?.classList.add('hidden');
  1123→
  1124→                if (typeof refreshCredits === 'function') {
  1125→                    await refreshCredits();
  1126→                }
  1127→
  1128→                const tierName = result.tier ? result.tier.charAt(0).toUpperCase() + result.tier.slice(1) : 'Unknown';
  1129→                setStatus(`License activated! ${tierName} tier`);
  1130→            } catch (err) {
