  1135→                debugLog('Error name:', err?.name);
  1136→                debugLog('Error message:', err?.message);
  1137→                debugLog('Error stack:', err?.stack);
  1138→                debugLog('Backend URL:', getBackendUrl());
  1139→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
  1140→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
  1141→                debugLog('User agent:', navigator?.userAgent);
  1142→                debugLog('Error constructor:', err?.constructor?.name);
  1143→                debugLog('Is TypeError:', err?.name === 'TypeError');
  1144→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
  1145→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
  1146→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
  1147→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
  1148→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
  1149→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
  1150→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
  1151→                debugLog('================================================');
  1152→
  1153→                // Provide detailed error message based on error type
  1154→                let errorMessage = 'Connection error. ';
  1155→
  1156→                if (err.message === 'Request timed out') {
  1157→                    errorMessage += 'Server is not responding. Check if backend is running.';
  1158→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
