   207→/**
   208→ * Initialize event listeners for sequence state changes
   209→ * This provides real-time updates when sequences are activated/deactivated
   210→ */
   211→function initSequenceEventListeners() {
   212→    if (!jsx.cs) {
   213→        console.warn('[SPLICE] Cannot init sequence listeners - CSInterface not available');
   214→        return;
   215→    }
   216→
   217→    try {
   218→        // Listen for sequence activation events
   219→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceActivated', async (event) => {
   220→            debugLog('Sequence activated event received');
   221→            try {
   222→                const seqInfo = await jsx.call('getActiveSequence');
   223→                if (seqInfo && !seqInfo.error) {
   224→                    window.spliceState.activeSequence = seqInfo;
   225→                    setStatus('Sequence: ' + (seqInfo.name || 'Unknown'));
   226→                    debugLog('Active sequence updated:', seqInfo.name);
   227→                }
   228→            } catch (e) {
   229→                console.warn('[SPLICE] Failed to get sequence info on activation:', e.message);
   230→            }
   231→        });
   232→
   233→        // Listen for sequence close/deactivation
   234→        jsx.cs.addEventListener('com.adobe.csxs.events.SequenceDeactivated', () => {
   235→            debugLog('Sequence deactivated event');
   236→            window.spliceState.activeSequence = null;
   237→            setStatus('No sequence open', true);
   238→        });
   239→
   240→        // Listen for project item selection changes (includes sequences)
   241→        jsx.cs.addEventListener('com.adobe.csxs.events.SelectionChanged', async () => {
   242→            // Debounce rapid selection changes
   243→            if (window._selectionChangeTimeout) {
   244→                clearTimeout(window._selectionChangeTimeout);
   245→            }
   246→            window._selectionChangeTimeout = setTimeout(async () => {
   247→                try {
   248→                    const hasSequence = await checkSequenceOpen(0, 0); // Quick check, no retries
   249→                    if (hasSequence && !window.spliceState.activeSequence) {
   250→                        const seqInfo = await jsx.call('getActiveSequence');
   251→                        if (seqInfo && !seqInfo.error) {
   252→                            window.spliceState.activeSequence = seqInfo;
   253→                            debugLog('Sequence detected via selection change:', seqInfo.name);
   254→                        }
   255→                    }
   256→                } catch (e) {
   257→                    // Ignore errors during selection change - it's just for proactive updates
   258→                }
   259→            }, 200);
   260→        });
   261→
   262→        debugLog('Sequence event listeners initialized');
   263→    } catch (e) {
   264→        console.error('[SPLICE] Failed to init sequence event listeners:', e.message);
   265→    }
   266→}
