  1022→async function seekToTime(seconds) {
  1023→    try {
  1024→        // Check if there's an active sequence before seeking
  1025→        if (!app?.project?.activeSequence) {
  1026→            debugLog('No active sequence to seek in');
  1027→            return;
  1028→        }
  1029→        // Use JSX to set playhead position
  1030→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1031→        const ticks = Math.round(seconds * 254016000000);
  1032→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
  1033→    } catch (e) {
  1034→        console.warn('[SPLICE] Seek failed:', e);
  1035→    }
  1036→}
