  1151→async function buildSequence() {
  1152→    if (isOperationInProgress) return;
  1153→
  1154→    isOperationInProgress = true;
  1155→    setStatus('Building sequence...');
  1156→
  1157→    try {
  1158→        const settings = getSettings();
  1159→
  1160→        // Ensure we have sequence info
  1161→        if (!currentSequenceInfo) {
  1162→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1163→        }
  1164→
  1165→        // Create cut list from selected silences
  1166→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1167→
  1168→        // Calculate total duration from sequence info or last silence
  1169→        let duration = 0;
  1170→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1171→            duration = currentSequenceInfo.duration;
  1172→        } else if (previewSilences.length > 0) {
  1173→            const lastSilence = previewSilences[previewSilences.length - 1];
  1174→            duration = lastSilence.end + 10; // Add buffer
  1175→        }
  1176→
  1177→        // Call backend to generate cut list
  1178→        const cutListResponse = await fetchWithTimeout(
  1179→            `${getBackendUrl()}/cut-list`,
  1180→            {
  1181→                method: 'POST',
  1182→                headers: getAuthHeaders(),
  1183→                body: JSON.stringify({
  1184→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1185→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1186→                    duration: duration,
  1187→                    silences: selectedSilences,
  1188→                    takes: previewTakes,
  1189→                    enableTakes: settings.enableTakesDetection,
  1190→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1191→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1192→                })
  1193→            }
  1194→        );
  1195→
  1196→        if (!cutListResponse.ok) {
  1197→            throw new Error(await parseErrorResponse(cutListResponse));
  1198→        }
  1199→
  1200→        const cutList = await cutListResponse.json();
  1201→
  1202→        // Build sequence via JSX
  1203→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1204→
  1205→        if (result && result.success) {
  1206→            setStatus(`Built sequence: ${result.sequenceName}`);
  1207→            hidePreview();
  1208→        } else {
  1209→            throw new Error(result?.error || 'Build failed');
  1210→        }
  1211→    } catch (error) {
  1212→        setStatus('Build failed: ' + error.message, true);
  1213→    } finally {
  1214→        isOperationInProgress = false;
  1215→    }
  1216→}
