   193→// ============================================================================
   194→// JSX CONNECTION CHECK
   195→// ============================================================================
   196→async function checkJSXConnection() {
   197→    try {
   198→        const result = await jsx.call('getVersion');
   199→        console.log('[SPLICE] JSX connection OK, version:', result);
   200→        return true;
   201→    } catch (e) {
   202→        console.warn('[SPLICE] JSX connection failed:', e.message);
   203→        setStatus('Premiere Pro connection failed', true);
   204→        return false;
   205→    }
   206→}
   207→
   208→async function checkSequenceOpen() {
   209→    try {
   210→        const result = await jsx.call('checkSequenceOpen');
   211→        return result === true || result === 'true';
   212→    } catch {
   213→        return false;
   214→    }
   215→}
