   217→// ============================================================================
   218→// OPTIONS TOGGLE
   219→// ============================================================================
   220→function initOptionsToggle() {
   221→    if (ui.optionsToggle && ui.optionsPanel) {
   222→        ui.optionsToggle.addEventListener('click', () => {
   223→            const isExpanded = ui.optionsPanel.classList.toggle('collapsed');
   224→            ui.optionsToggle.classList.toggle('expanded', !isExpanded);
   225→            saveSettings({ expandedOptions: !isExpanded });
   226→        });
   227→
   228→        // Restore state
   229→        const settings = getSettings();
   230→        if (!settings.expandedOptions) {
   231→            ui.optionsPanel.classList.add('collapsed');
   232→        } else {
   233→            ui.optionsToggle.classList.add('expanded');
   234→        }
   235→    }
   236→}
