   238→// ============================================================================
   239→// TEXT EDITOR TOGGLE
   240→// ============================================================================
   241→function initTextEditorToggle() {
   242→    const toggle = document.getElementById('textEditorToggle');
   243→    const panel = document.getElementById('text-editor-panel');
   244→
   245→    if (toggle && panel) {
   246→        toggle.addEventListener('click', () => {
   247→            const isCollapsed = panel.classList.toggle('collapsed');
   248→            const icon = toggle.querySelector('.toggle-icon');
   249→            if (icon) {
   250→                icon.textContent = isCollapsed ? '+' : '-';
