   292→// ============================================================================
   293→// SLIDERS
   294→// ============================================================================
   295→function initSliders() {
   296→    // Sensitivity slider
   297→    if (ui.sensitivitySlider && ui.sensitivityValue) {
   298→        ui.sensitivitySlider.addEventListener('input', () => {
   299→            ui.sensitivityValue.textContent = ui.sensitivitySlider.value;
   300→            saveSettings({ sensitivity: parseInt(ui.sensitivitySlider.value) });
   301→        });
   302→    }
   303→
   304→    // J-Cut sliders
   305→    if (ui.jcutLeadIn && ui.jcutLeadInValue) {
   306→        ui.jcutLeadIn.addEventListener('input', () => {
   307→            const val = (ui.jcutLeadIn.value / 100).toFixed(2);
   308→            ui.jcutLeadInValue.textContent = val + 's';
   309→            saveSettings({ jcutLeadIn: parseFloat(val) });
   310→        });
   311→    }
   312→
   313→    if (ui.jcutLeadOut && ui.jcutLeadOutValue) {
   314→        ui.jcutLeadOut.addEventListener('input', () => {
   315→            const val = (ui.jcutLeadOut.value / 100).toFixed(2);
   316→            ui.jcutLeadOutValue.textContent = val + 's';
   317→            saveSettings({ jcutLeadOut: parseFloat(val) });
   318→        });
   319→    }
   320→}
