   361→            saveSettings({ enableProfanity: ui.enableProfanity.checked });
   362→        });
   363→    }
   364→
   365→    // Filler word detection toggle
   366→    if (ui.enableFillerDetection && ui.fillerSettings) {
   367→        ui.enableFillerDetection.addEventListener('change', () => {
   368→            ui.fillerSettings.classList.toggle('collapsed', !ui.enableFillerDetection.checked);
   369→            saveSettings({ enableFillerDetection: ui.enableFillerDetection.checked });
