   374→// ============================================================================
   375→// PRESET SELECTOR
   376→// ============================================================================
   377→// PRESETS is defined in config.js
   378→
   379→function initPresetSelector() {
   380→    if (!ui.presetSelector) return;
   381→
   382→    ui.presetSelector.addEventListener('change', () => {
   383→        const presetId = ui.presetSelector.value;
   384→        applyPreset(presetId);
   385→    });
   386→
   387→    // Load current preset
   388→    const settings = getSettings();
   389→    if (settings.activePreset) {
   390→        ui.presetSelector.value = settings.activePreset;
   391→    }
   392→}
   393→
   394→function applyPreset(presetId) {
   395→    const preset = PRESETS[presetId];
   396→    if (!preset) return;
   397→
   398→    // Custom preset - keep current settings
   399→    if (presetId === 'custom' || !preset.settings) {
   400→        saveSettings({ activePreset: 'custom' });
   401→        setStatus('Using custom settings');
   402→        return;
   403→    }
   404→
   405→    const settings = preset.settings;
   406→
   407→    // Apply to UI
   408→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   409→        ui.sensitivitySlider.value = settings.sensitivity;
   410→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   411→    }
   412→
   413→    if (ui.enableJCut) {
   414→        ui.enableJCut.checked = settings.enableJCut || false;
   415→        if (ui.jcutSettings) {
   416→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   417→        }
   418→    }
   419→
   420→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   421→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   422→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   423→    }
   424→
   425→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   426→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   427→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   428→    }
   429→
   430→    // Save merged settings
   431→    saveSettings({
   432→        activePreset: presetId,
   433→        sensitivity: settings.sensitivity,
   434→        threshold: settings.threshold,
   435→        minSilenceLength: settings.minSilenceLength,
   436→        paddingStart: settings.paddingStart,
   437→        paddingEnd: settings.paddingEnd,
   438→        enableTakesDetection: settings.enableTakesDetection,
   439→        enableJCut: settings.enableJCut || false,
   440→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   441→        jcutLeadOut: settings.jCutLeadOut || 0.2
   442→    });
   443→
   444→    setStatus(`Applied ${preset.name} preset`);
   445→}
   446→
   447→// ============================================================================
   448→// LOAD SETTINGS TO UI
   449→// ============================================================================
   450→function loadSettingsToUI() {
   451→    const settings = getSettings();
   452→
   453→    if (ui.sensitivitySlider) {
   454→        ui.sensitivitySlider.value = settings.sensitivity;
   455→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   456→    }
   457→
   458→    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   459→    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   460→    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   461→    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   462→
   463→    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   464→    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   465→    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   466→
   467→    if (ui.jcutLeadIn) {
   468→        ui.jcutLeadIn.value = Math.round(settings.jcutLeadIn * 100);
   469→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jcutLeadIn + 's';
   470→    }
   471→    if (ui.jcutLeadOut) {
   472→        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
   473→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
   474→    }
   475→
   476→    if (ui.presetSelector && settings.activePreset) {
   477→        ui.presetSelector.value = settings.activePreset;
   478→    }
   479→}
