  1202→    try {
  1203→        const response = await fetchWithTimeout(
  1204→            `${getBackendUrl()}/license/lookup`,
  1205→            {
  1206→                method: 'POST',
  1207→                headers: { 'Content-Type': 'application/json' },
  1208→                body: JSON.stringify({ email })
  1209→            },
  1210→            30000
  1211→        );
  1212→
  1213→        const result = await response.json();
  1214→
  1215→        // Note: For security, the backend does NOT return the license key directly.
  1216→        // It sends the key via email to prevent enumeration attacks.
  1217→        if (result.success) {
  1218→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1219→        } else {
  1220→            setStatus(result.error || 'Lookup failed. Please try again.', true);
