   143→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   144→    const controller = new AbortController();
   145→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   146→
