   677→        if (!window.spliceBuilder) {
   678→            throw new Error('Builder not initialized');
   679→        }
   680→
   681→        // Get active sequence info
   682→        const seqInfo = await jsx.call('getActiveSequence');
   683→        if (!seqInfo) {
   684→            throw new Error('No active sequence');
   685→        }
   686→
   687→        // Create cut list from decisions
   688→        const cutList = {
   689→            version: '3.5',
