   680→function isValidYouTubeUrl(url) {
   681→  if (!url) return false;
   682→  const patterns = [
   683→    /youtube\.com\/watch\?v=[\w-]{11}/,
   684→    /youtu\.be\/[\w-]{11}/,
   685→    /youtube\.com\/embed\/[\w-]{11}/
   686→  ];
   687→  return patterns.some(p => p.test(url));
   688→}
