  1355→function moveClipByNTracks(currentTrackId, clipStartTime, nTracksToMove, trackType) {
  1356→    if (!isQEAvailable() && !enableQE()) {
  1357→        return JSON.stringify({ error: "QE not available for moving clips between tracks" });
  1358→    }
  1359→
  1360→    try {
  1361→        var accuracy = 3;
  1362→        var search_start_time = clipStartTime.toFixed(accuracy);
  1363→
  1364→        if (trackType == "audio") {
  1365→            var numClipsInTrack = qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).numItems;
  1366→            for (var j = 0; j < numClipsInTrack; j++) {
  1367→                if (qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1368→                    qe.project.getActiveSequence().getAudioTrackAt(currentTrackId).getItemAt(j).moveToTrack(0, nTracksToMove, "0");
  1369→                    return JSON.stringify({ success: true });
  1370→                }
  1371→            }
  1372→        } else if (trackType == "video") {
  1373→            var numClipsInTrack = qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).numItems;
  1374→            for (var j = 0; j < numClipsInTrack; j++) {
  1375→                if (qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).start.secs.toFixed(accuracy) == search_start_time) {
  1376→                    qe.project.getActiveSequence().getVideoTrackAt(currentTrackId).getItemAt(j).moveToTrack(nTracksToMove, 0, "0");
  1377→                    return JSON.stringify({ success: true });
  1378→                }
  1379→            }
  1380→        }
  1381→        return JSON.stringify({ error: "Clip not found at specified time" });
