    19→const BACKEND_API_URL =
    20→  process.env.BACKEND_API_URL || "https://splice-api-production.up.railway.app";
    21→
    22→export async function POST(request: NextRequest) {
    23→  const body = await request.text();
    24→  const signature = request.headers.get("stripe-signature");
    25→
    26→  if (!signature) {
    27→    return NextResponse.json(
    28→      { error: "Missing stripe-signature header" },
    29→      { status: 400 }
    30→    );
    31→  }
    32→
    33→  let event: Stripe.Event;
    34→
    35→  try {
    36→    event = getStripe().webhooks.constructEvent(body, signature, webhookSecret);
    37→  } catch (error) {
    38→    console.error("Webhook signature verification failed:", error);
    39→    return NextResponse.json(
    40→      {
    41→        error:
    42→          error instanceof Error ? error.message : "Webhook signature verification failed",
    43→      },
    44→      { status: 400 }
    45→    );
    46→  }
    47→
    48→  // Handle the event
    49→  try {
    50→    switch (event.type) {
    51→      case "checkout.session.completed": {
    52→        const session = event.data.object as Stripe.Checkout.Session;
    53→        await handleCheckoutComplete(session);
    54→        break;
    55→      }
    56→
    57→      case "customer.subscription.created":
    58→      case "customer.subscription.updated": {
