import { NextRequest, NextResponse } from "next/server";
import Stripe from "stripe";

// Lazy initialization to avoid errors during build
let stripe: Stripe | null = null;
function getStripe(): Stripe {
  if (!stripe) {
    if (!process.env.STRIPE_SECRET_KEY) {
      throw new Error("STRIPE_SECRET_KEY is not configured");
    }
    stripe = new Stripe(process.env.STRIPE_SECRET_KEY);
  }
  return stripe;
}

const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET || "";

// Backend API URL for forwarding events
const BACKEND_API_URL =
  process.env.BACKEND_API_URL || "https://splice-api-production.up.railway.app";

export async function POST(request: NextRequest) {
  const body = await request.text();
  const signature = request.headers.get("stripe-signature");

  if (!signature) {
    return NextResponse.json(
      { error: "Missing stripe-signature header" },
      { status: 400 }
    );
  }

  let event: Stripe.Event;

  try {
    event = getStripe().webhooks.constructEvent(body, signature, webhookSecret);
  } catch (error) {
    console.error("Webhook signature verification failed:", error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : "Webhook signature verification failed",
      },
      { status: 400 }
    );
  }

  // Handle the event
  try {
    switch (event.type) {
      case "checkout.session.completed": {
        const session = event.data.object as Stripe.Checkout.Session;
        await handleCheckoutComplete(session);
        break;
      }

      case "customer.subscription.created":
      case "customer.subscription.updated": {
        const subscription = event.data.object as Stripe.Subscription;
        await handleSubscriptionUpdate(subscription);
        break;
      }

      case "customer.subscription.deleted": {
        const subscription = event.data.object as Stripe.Subscription;
        await handleSubscriptionCanceled(subscription);
        break;
      }

      case "invoice.payment_succeeded": {
        const invoice = event.data.object as Stripe.Invoice;
        await handleInvoicePaid(invoice);
        break;
      }

      case "invoice.payment_failed": {
        const invoice = event.data.object as Stripe.Invoice;
        await handleInvoicePaymentFailed(invoice);
        break;
      }

      default:
        console.log(`Unhandled event type: ${event.type}`);
    }

    // Forward the event to the backend API for processing
    await forwardToBackend(event);

    return NextResponse.json({ received: true });
  } catch (error) {
    console.error("Error processing webhook:", error);
    return NextResponse.json(
      {
        error:
          error instanceof Error ? error.message : "Error processing webhook",
      },
      { status: 500 }
    );
  }
}

async function handleCheckoutComplete(session: Stripe.Checkout.Session) {
  console.log("Checkout completed:", {
    sessionId: session.id,
    customerEmail: session.customer_details?.email,
    plan: session.metadata?.plan,
    subscriptionId: session.subscription,
  });

  // Here you could:
  // - Send welcome email
  // - Provision user account
  // - Grant access to the plugin
}

async function handleSubscriptionUpdate(subscription: Stripe.Subscription) {
  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  const sub = subscription as any;
  console.log("Subscription updated:", {
    subscriptionId: subscription.id,
    status: subscription.status,
    customerId: subscription.customer,
    currentPeriodEnd: sub.current_period_end
      ? new Date(sub.current_period_end * 1000)
      : null,
  });

  // Here you could:
  // - Update user's plan in database
  // - Adjust feature access
}

async function handleSubscriptionCanceled(subscription: Stripe.Subscription) {
  console.log("Subscription canceled:", {
    subscriptionId: subscription.id,
    customerId: subscription.customer,
  });

  // Here you could:
  // - Revoke access at end of billing period
  // - Send cancellation confirmation
  // - Trigger win-back campaign
}

async function handleInvoicePaid(invoice: Stripe.Invoice) {
  console.log("Invoice paid:", {
    invoiceId: invoice.id,
    customerId: invoice.customer,
    amount: invoice.amount_paid,
  });

  // Here you could:
  // - Send receipt
  // - Reset usage limits
  // - Update billing history
}

async function handleInvoicePaymentFailed(invoice: Stripe.Invoice) {
  console.log("Invoice payment failed:", {
    invoiceId: invoice.id,
    customerId: invoice.customer,
    amount: invoice.amount_due,
  });

  // Here you could:
  // - Send payment failure notification
  // - Retry payment
  // - Pause account if needed
}

async function forwardToBackend(event: Stripe.Event) {
  try {
    const response = await fetch(`${BACKEND_API_URL}/webhooks/stripe`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "X-Webhook-Source": "splice-website",
      },
      body: JSON.stringify(event),
    });

    if (!response.ok) {
      console.error("Failed to forward webhook to backend:", await response.text());
    }
  } catch (error) {
    console.error("Error forwarding webhook to backend:", error);
  }
}

"use client";

import { useEffect, useState, Suspense } from "react";
import { useSearchParams, useRouter } from "next/navigation";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { motion } from "framer-motion";
import { Check, Loader2, CreditCard, Shield, ArrowLeft } from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

const plans: Record<
  string,
  {
    name: string;
    price: string;
    period: string;
    features: string[];
    popular?: boolean;
  }
> = {
  starter: {
    name: "Starter",
    price: "$19",
    period: "/month",
    features: [
      "12 hours of processing/month",
      "One-click silence removal",
      "5 AI music generations/month",
      "Basic caption templates",
      "Email support",
    ],
  },
  pro: {
    name: "Pro",
    price: "$49",
    period: "/month",
    popular: true,
    features: [
      "30 hours of processing/month",
      "45 min vocal isolation included",
      "20 AI music generations/month",
      "All 5 caption templates",
      "Social reframe & text editing",
      "Priority support",
    ],
  },
  team: {
    name: "Team",
    price: "$149",
    period: "/month",
    features: [
      "100 hours of processing/month",
      "3 hours vocal isolation included",
      "100 AI music generations/month",
      "All features included",
      "Dedicated account manager",
    ],
  },
};

function CheckoutContent() {
  const searchParams = useSearchParams();
  const router = useRouter();
  const planId = searchParams.get("plan") || "pro";
  const plan = plans[planId] || plans.pro;

  const [email, setEmail] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleCheckout = async () => {
    if (!email) {
      setError("Please enter your email address");
      return;
    }

    if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
      setError("Please enter a valid email address");
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch("/api/checkout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          plan: planId,
          email,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || "Failed to create checkout session");
      }

      // Redirect to Stripe checkout
      if (data.url) {
        window.location.href = data.url;
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : "Something went wrong");
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen dark bg-black text-white">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4">
          <div className="max-w-4xl mx-auto">
            {/* Back link */}
            <Link
              href="/pricing"
              className="inline-flex items-center text-sm text-muted-foreground hover:text-foreground mb-8"
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to pricing
            </Link>

            <div className="grid md:grid-cols-2 gap-8">
              {/* Order summary */}
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5 }}
              >
                <Card>
                  <CardHeader>
                    <CardTitle>Order Summary</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="flex items-start justify-between mb-6">
                      <div>
                        <div className="flex items-center gap-2">
                          <h3 className="text-xl font-bold">{plan.name}</h3>
                          {plan.popular && (
                            <Badge className="bg-gradient-to-r from-violet-600 to-cyan-600">
                              Popular
                            </Badge>
                          )}
                        </div>
                        <p className="text-muted-foreground text-sm mt-1">
                          14-day free trial included
                        </p>
                      </div>
                      <div className="text-right">
                        <span className="text-2xl font-bold">{plan.price}</span>
                        <span className="text-muted-foreground">
                          {plan.period}
                        </span>
                      </div>
                    </div>

                    <ul className="space-y-2 mb-6">
                      {plan.features.map((feature) => (
                        <li
                          key={feature}
                          className="flex items-start gap-2 text-sm"
                        >
                          <Check className="w-4 h-4 text-green-500 shrink-0 mt-0.5" />
                          {feature}
                        </li>
                      ))}
                    </ul>

                    <div className="border-t border-border pt-4">
                      <div className="flex justify-between mb-2">
                        <span>Trial period</span>
                        <span className="font-medium">14 days free</span>
                      </div>
                      <div className="flex justify-between text-lg font-bold">
                        <span>Due today</span>
                        <span className="text-green-600">$0.00</span>
                      </div>
                      <p className="text-xs text-muted-foreground mt-2">
                        You won&apos;t be charged until your trial ends.
                        Cancel anytime.
                      </p>
                    </div>
                  </CardContent>
                </Card>

                {/* Trust badges */}
                <div className="mt-4 flex items-center gap-4 text-sm text-muted-foreground">
                  <div className="flex items-center gap-1">
                    <Shield className="w-4 h-4" />
                    <span>SSL Secured</span>
                  </div>
                  <div className="flex items-center gap-1">
                    <CreditCard className="w-4 h-4" />
                    <span>Powered by Stripe</span>
                  </div>
                </div>
              </motion.div>

              {/* Checkout form */}
              <motion.div
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ duration: 0.5, delay: 0.1 }}
              >
                <Card>
                  <CardHeader>
                    <CardTitle>Start Your Free Trial</CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      <div>
                        <label
                          htmlFor="email"
                          className="block text-sm font-medium mb-2"
                        >
                          Email address
                        </label>
                        <Input
                          id="email"
                          type="email"
                          placeholder="you@example.com"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                          className="w-full"
                        />
                        <p className="text-xs text-muted-foreground mt-1">
                          We&apos;ll send your license key to this email
                        </p>
                      </div>

                      {error && (
                        <div className="bg-red-500/10 border border-red-500/20 rounded-lg p-3 text-sm text-red-600">
                          {error}
                        </div>
                      )}

                      <Button
                        className="w-full bg-gradient-to-r from-violet-600 to-cyan-600 hover:from-violet-700 hover:to-cyan-700"
                        size="lg"
                        onClick={handleCheckout}
                        disabled={isLoading}
                      >
                        {isLoading ? (
                          <>
                            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                            Processing...
                          </>
                        ) : (
                          <>
                            <CreditCard className="w-4 h-4 mr-2" />
                            Start Free Trial
                          </>
                        )}
                      </Button>

                      <p className="text-xs text-center text-muted-foreground">
                        By continuing, you agree to our{" "}
                        <Link
                          href="/terms"
                          className="text-primary hover:underline"
                        >
                          Terms of Service
                        </Link>{" "}
                        and{" "}
                        <Link
                          href="/privacy"
                          className="text-primary hover:underline"
                        >
                          Privacy Policy
                        </Link>
                        .
                      </p>
                    </div>
                  </CardContent>
                </Card>

                {/* FAQ */}
                <div className="mt-6 space-y-4">
                  <h4 className="font-semibold">Common Questions</h4>
                  <div className="space-y-3 text-sm">
                    <div>
                      <p className="font-medium">
                        When will I be charged?
                      </p>
                      <p className="text-muted-foreground">
                        Your 14-day free trial starts immediately. You&apos;ll
                        only be charged after the trial ends.
                      </p>
                    </div>
                    <div>
                      <p className="font-medium">Can I cancel anytime?</p>
                      <p className="text-muted-foreground">
                        Yes, you can cancel your subscription at any time from
                        your account settings.
                      </p>
                    </div>
                    <div>
                      <p className="font-medium">
                        How do I get my license key?
                      </p>
                      <p className="text-muted-foreground">
                        Your license key will be sent to your email immediately
                        after signup.
                      </p>
                    </div>
                  </div>
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default function CheckoutPage() {
  return (
    <Suspense
      fallback={
        <div className="min-h-screen flex items-center justify-center">
          <Loader2 className="w-8 h-8 animate-spin text-primary" />
        </div>
      }
    >
      <CheckoutContent />
    </Suspense>
  );
}

