"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import Image from "next/image";
import { usePathname, useRouter } from "next/navigation";
import { Button } from "@/components/ui/button";
import { Menu, X } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

export function Header() {
  const [isScrolled, setIsScrolled] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const pathname = usePathname();
  const router = useRouter();

  useEffect(() => {
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  const scrollToSection = (e: React.MouseEvent<HTMLAnchorElement>, href: string) => {
    // Only handle hash links on the home page
    if (href.startsWith("#")) {
      e.preventDefault();

      // If not on home page, navigate to home first
      if (pathname !== "/") {
        router.push("/" + href);
        return;
      }

      const element = document.querySelector(href);
      if (element) {
        const headerOffset = 80;
        const elementPosition = element.getBoundingClientRect().top;
        const offsetPosition = elementPosition + window.pageYOffset - headerOffset;

        window.scrollTo({
          top: offsetPosition,
          behavior: "smooth"
        });
      }
      setIsMobileMenuOpen(false);
    }
  };

  const navLinks = [
    { href: "#features", label: "Features" },
    { href: "#comparison", label: "Compare" },
    { href: "#pricing", label: "Pricing" },
    { href: "/download", label: "Download" },
    { href: "/docs", label: "Docs" },
  ];

  return (
    <header
      className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${isScrolled
        ? "bg-black/80 backdrop-blur-md border-b border-white/10 py-3 shadow-lg"
        : "bg-transparent py-4"
        }`}
    >
      <div className="container mx-auto px-4 flex items-center justify-between">
        {/* Logo */}
        <Link href="/" className="flex items-center gap-2">
          <div className="w-8 h-8 neon-glow">
            <Image
              src="/images/logo.svg"
              alt="SPLICE Logo"
              width={32}
              height={32}
              className="w-full h-full object-contain"
            />
          </div>
          <span className="font-bold text-xl text-white">SPLICE</span>
        </Link>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center gap-8">
          {navLinks.map((link) => (
            <a
              key={link.href}
              href={link.href}
              onClick={(e) => scrollToSection(e, link.href)}
              className="text-sm font-medium text-muted-foreground hover:text-foreground transition-colors cursor-pointer"
            >
              {link.label}
            </a>
          ))}
        </nav>

        {/* Desktop CTAs */}
        <div className="hidden md:flex items-center gap-4">
          <Button
            variant="outline"
            size="sm"
            className="text-white border-white/20 hover:bg-white/10 hover:text-white"
            asChild
          >
            <Link href="/login">Log In</Link>
          </Button>
          <Button
            size="sm"
            className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold btn-glow"
            asChild
          >
            <a
              href="#pricing"
              onClick={(e) => scrollToSection(e, "#pricing")}
            >
              Start Free Trial
            </a>
          </Button>
        </div>

        {/* Mobile Menu Button */}
        <button
          className="md:hidden p-2"
          onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
          aria-label="Toggle menu"
        >
          {isMobileMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Menu */}
      <AnimatePresence>
        {isMobileMenuOpen && (
          <motion.div
            initial={{ opacity: 0, height: 0 }}
            animate={{ opacity: 1, height: "auto" }}
            exit={{ opacity: 0, height: 0 }}
            className="md:hidden glass border-t border-border/50 bg-black/95 backdrop-blur-xl"
          >
            <nav className="container mx-auto px-4 py-4 flex flex-col gap-4">
              {navLinks.map((link) => (
                <a
                  key={link.href}
                  href={link.href}
                  onClick={(e) => scrollToSection(e, link.href)}
                  className="text-sm font-medium py-2 cursor-pointer"
                >
                  {link.label}
                </a>
              ))}
              <div className="flex flex-col gap-2 pt-4 border-t border-border/50">
                <Button variant="outline" size="sm" asChild>
                  <Link href="/login">Log In</Link>
                </Button>
                <Button
                  size="sm"
                  className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                  onClick={(e) => {
                    e.preventDefault();
                    const element = document.querySelector("#pricing");
                    if (element) {
                      element.scrollIntoView({ behavior: "smooth" });
                    }
                    setIsMobileMenuOpen(false);
                  }}
                >
                  Start Free Trial
                </Button>
              </div>
            </nav>
          </motion.div>
        )}
      </AnimatePresence>
    </header>
  );
}

"use client";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Play, Zap, Clock, Sparkles, Star, Shield, Users, X, Music, CreditCard, RefreshCcw, Download } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import Link from "next/link";
import { useState } from "react";


// Demo video configuration - set the URL to enable video playback
// Supports: YouTube (youtube.com/watch?v=...), Vimeo (vimeo.com/...), or self-hosted (.mp4, .webm)
const DEMO_VIDEO_URL = "/videos/splice-demo.mp4";

// ... (keep helper if needed, or simplify)

// ... inside render:
{/* Demo Video Container */ }
<div className="aspect-video w-full h-full bg-[#0d1829] overflow-hidden">
  <video
    src={DEMO_VIDEO_URL}
    controls
    autoPlay
    className="w-full h-full object-contain"
    poster="/hero-dashboard-preview.jpg"
  >
    Your browser does not support the video tag.
  </video>
</div>

// Pre-generated waveform data to avoid hydration mismatch from Math.random()
const WAVEFORM_BARS = [
  { h: 45, o: 1 }, { h: 72, o: 0.5 }, { h: 38, o: 1 }, { h: 85, o: 0.5 }, { h: 52, o: 1 },
  { h: 28, o: 0.5 }, { h: 67, o: 1 }, { h: 43, o: 0.5 }, { h: 91, o: 1 }, { h: 35, o: 0.5 },
  { h: 78, o: 1 }, { h: 55, o: 0.5 }, { h: 32, o: 1 }, { h: 88, o: 0.5 }, { h: 48, o: 1 },
  { h: 25, o: 0.5 }, { h: 73, o: 1 }, { h: 62, o: 0.5 }, { h: 95, o: 1 }, { h: 40, o: 0.5 },
  { h: 58, o: 1 }, { h: 82, o: 0.5 }, { h: 36, o: 1 }, { h: 69, o: 0.5 }, { h: 50, o: 1 },
  { h: 29, o: 0.5 }, { h: 76, o: 1 }, { h: 44, o: 0.5 }, { h: 87, o: 1 }, { h: 33, o: 0.5 },
  { h: 65, o: 1 }, { h: 53, o: 0.5 }, { h: 92, o: 1 }, { h: 41, o: 0.5 }, { h: 70, o: 1 },
  { h: 27, o: 0.5 }, { h: 80, o: 1 }, { h: 57, o: 0.5 }, { h: 34, o: 1 }, { h: 83, o: 0.5 },
  { h: 46, o: 1 }, { h: 68, o: 0.5 }, { h: 39, o: 1 }, { h: 90, o: 0.5 }, { h: 54, o: 1 },
  { h: 31, o: 0.5 }, { h: 75, o: 1 }, { h: 60, o: 0.5 }, { h: 86, o: 1 }, { h: 42, o: 0.5 },
  { h: 63, o: 1 }, { h: 79, o: 0.5 }, { h: 37, o: 1 }, { h: 71, o: 0.5 }, { h: 49, o: 1 },
  { h: 26, o: 0.5 }, { h: 84, o: 1 }, { h: 56, o: 0.5 }, { h: 93, o: 1 }, { h: 47, o: 0.5 },
];

export function Hero() {
  const [showDemo, setShowDemo] = useState(false);

  return (
    <section className="relative min-h-screen flex items-center justify-center pt-20 pb-16 overflow-hidden dark">
      {/* Background gradient */}
      <div className="absolute inset-0 hero-gradient" />

      {/* Grid pattern */}
      <div className="absolute inset-0 grid-pattern opacity-50" />

      {/* Animated background elements - hidden on mobile for performance */}
      <div className="absolute inset-0 overflow-hidden hidden sm:block">
        <motion.div
          className="absolute top-1/4 left-1/4 w-[500px] h-[500px] bg-[#00D4FF]/10 rounded-full blur-[100px] will-change-transform"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.3, 0.5, 0.3],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-[400px] h-[400px] bg-[#00A8CC]/10 rounded-full blur-[100px] will-change-transform"
          animate={{
            scale: [1.2, 1, 1.2],
            opacity: [0.5, 0.3, 0.5],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="max-w-4xl mx-auto text-center">
          {/* Social Proof Badge */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="flex items-center justify-center gap-4 mb-6"
          >
            <Badge
              variant="outline"
              className="px-4 py-1.5 text-sm font-medium border-green-500/50 bg-green-500/10 text-green-400"
            >
              <Shield className="w-3.5 h-3.5 mr-1.5" />
              30-Day Money-Back Guarantee
            </Badge>
            <div className="hidden sm:flex items-center gap-1 text-sm text-gray-400">
              <div className="flex -space-x-2">
                {[1, 2, 3, 4].map((i) => (
                  <div
                    key={i}
                    className="w-6 h-6 rounded-full bg-gradient-to-br from-[#00D4FF] to-[#0099CC] border-2 border-[#060b14] flex items-center justify-center text-[10px] font-bold text-black"
                  >
                    {String.fromCharCode(64 + i)}
                  </div>
                ))}
              </div>
              <span className="ml-2">
                <span className="text-white font-semibold">12,847</span> hours saved this week
              </span>
            </div>
          </motion.div>

          {/* Main Headline */}
          <motion.h1
            className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold tracking-tight mb-6 text-white"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            Cut Your Editing Time in Half.{" "}
