"use client";

import { useState } from "react";
import Link from "next/link";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { motion } from "framer-motion";

export default function LoginPage() {
    const [email, setEmail] = useState("");
    const [licenseKey, setLicenseKey] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    const handleLogin = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsLoading(true);
        // Mock login delay
        await new Promise((resolve) => setTimeout(resolve, 1500));
        setIsLoading(false);
        alert("This is a demo login page.");
    };

    return (
        <div className="min-h-screen bg-black text-white flex flex-col font-sans">
            <Header />

            <main className="flex-grow flex items-center justify-center pt-24 pb-12 px-4 relative overflow-hidden">
                {/* Background Gradients */}
                <div className="absolute top-0 left-1/4 w-[500px] h-[500px] bg-primary/20 rounded-full blur-[120px] pointer-events-none" />
                <div className="absolute bottom-0 right-1/4 w-[500px] h-[500px] bg-purple-500/10 rounded-full blur-[120px] pointer-events-none" />

                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="w-full max-w-md space-y-8 relative z-10"
                >
                    <div className="text-center space-y-2">
                        <h1 className="text-3xl font-bold tracking-tight">Welcome Back</h1>
                        <p className="text-muted-foreground">Enter your license key to manage your subscription.</p>
                    </div>

                    <div className="glass p-8 rounded-2xl border border-white/10 shadow-2xl backdrop-blur-xl">
                        <form onSubmit={handleLogin} className="space-y-6">
                            <div className="space-y-2">
                                <label htmlFor="email" className="text-sm font-medium text-gray-300">Email</label>
                                <Input
                                    id="email"
                                    type="email"
                                    placeholder="you@example.com"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                />
                            </div>

                            <div className="space-y-2">
                                <label htmlFor="license" className="text-sm font-medium text-gray-300">License Key</label>
                                <Input
                                    id="license"
                                    type="text"
                                    placeholder="SPLICE-XXXX-XXXX"
                                    value={licenseKey}
                                    onChange={(e) => setLicenseKey(e.target.value)}
                                    className="bg-black/40 border-white/10 text-white placeholder:text-gray-500 focus:border-primary/50"
                                    required
                                />
                            </div>

                            <Button
                                type="submit"
                                className="w-full bg-primary hover:bg-primary/90 text-black font-semibold h-11"
                                disabled={isLoading}
                            >
                                {isLoading ? "Verifying..." : "Access Dashboard"}
                            </Button>
                        </form>

                        <div className="mt-6 text-center text-sm text-gray-400">
                            <p>Don&apos;t have a license? <Link href="/#pricing" className="text-primary hover:underline">Get SPLICE</Link></p>
                        </div>
                    </div>
                </motion.div>
            </main>

            <Footer />
        </div>
    );
}

"use client";

import { Card, CardContent } from "@/components/ui/card";
import { motion } from "framer-motion";
import {
  VolumeX,
  Layers,
  Music,
  MessageSquare,
  Scissors,
  Smartphone,
  Check,
  X,
} from "lucide-react";

const features = [
  {
    icon: VolumeX,
    title: "Silence Removal",
    description:
      "Nondestructive text-based editing. Instantly detect and cut pauses and filler words with frame-accurate precision.",
    stats: "Save 3+ hours per edit",
  },
  {
    icon: Layers,
    title: "Vocal Isolation",
    description:
      "Studio-grade audio separation. Isolate dialogue from background noise or remove music stems with zero artifacting.",
    stats: "Dolby-standard clarity",
  },
  {
    icon: Music,
    title: "AI Music Scoring",
    description:
      "Generate adaptive soundtracks that perfectly time to your cut. Royalty-free accompaniment generated instantly on-device.",
    stats: "Unlimited local generation",
  },
  {
    icon: Scissors,
    title: "Text-Based Trimming",
    description:
      "Edit video via transcript. Delete dialogue in the text editor and the timeline updates instantly. 100% sync accuracy.",
    stats: "Rough cut in minutes",
  },
  {
    icon: Smartphone,
    title: "Smart Reframe",
    description:
      "Intelligent subject tracking for social deliverables. Automatically reframe generic ratios (16:9) to vertical (9:16) while keeping subjects centered.",
    stats: "One-click deliverables",
  },
  {
    icon: MessageSquare,
    title: "Dynamic Captions",
    description:
      "Professional captioning with custom style support. Animate, color-grade, and sync subtitles directly on the timeline.",
    stats: "+40% viewer retention",
  },
];

const container = {
  hidden: { opacity: 0 },
  show: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
    },
  },
};

const item = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 },
};

export function Features() {
  return (
    <section id="features" className="py-24 relative overflow-hidden bg-[#060b14] dark">
      {/* Background decoration */}
      <div className="absolute inset-0 grid-pattern opacity-30" />

      <div className="container mx-auto px-4 relative z-10">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-16"
