                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${selectedVersion === "cep"
                        ? "bg-[#00D4FF] text-black"
                        : "text-gray-300 hover:bg-white/10"
                        }`}
                      onClick={() => setSelectedVersion("cep")}
                    >
                      CEP
                    </button>
                  </div>
                </div>

                <div className="flex items-center gap-2 justify-center">
                  <span className="text-sm text-gray-400">OS:</span>
                  <div className="inline-flex rounded-lg border border-gray-700 p-1 bg-[#0d1420]">
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${selectedOS === "mac"
                        ? "bg-[#00D4FF] text-black"
                        : "text-gray-300 hover:bg-white/10"
                        }`}
                      onClick={() => setSelectedOS("mac")}
                    >
                      <Apple className="w-4 h-4" />
                      macOS
                    </button>
                    <button
                      className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${selectedOS === "windows"
                        ? "bg-[#00D4FF] text-black"
                        : "text-gray-300 hover:bg-white/10"
                        }`}
                      onClick={() => setSelectedOS("windows")}
                    >
                      <MonitorCog className="w-4 h-4" />
                      Windows
                    </button>
                  </div>
                </div>
              </div>

              {/* Steps */}
              <Card className="bg-[#0d1420] border-gray-800">
                <CardContent className="p-6">
                  <div className="space-y-6">
                    {currentSteps.map((step, index) => (
                      <div key={step.step}>
                        <div className="flex items-start gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {step.step}
                          </div>
                          <div className="flex-1">
                            <h3 className="font-semibold mb-1 text-white">{step.title}</h3>
                            <p className="text-sm text-gray-400 mb-2">
                              {step.description}
                            </p>
                            {step.code && (
                              <div className="relative">
                                <pre className="bg-[#0a0f1a] border border-gray-700 rounded-lg p-3 text-sm font-mono overflow-x-auto text-gray-300">
                                  {step.code}
                                </pre>
                                <button
                                  className="absolute top-2 right-2 p-1.5 rounded-md hover:bg-white/10 transition-colors"
                                  onClick={() =>
                                    copyToClipboard(step.code!, step.step)
                                  }
                                  aria-label="Copy to clipboard"
                                >
                                  {copiedStep === step.step ? (
                                    <CheckCircle className="w-4 h-4 text-[#00D4FF]" />
                                  ) : (
                                    <Copy className="w-4 h-4 text-gray-500" />
                                  )}
                                </button>
                              </div>
                            )}
                          </div>
                        </div>
                        {index < currentSteps.length - 1 && (
                          <Separator className="my-4 ml-12 bg-gray-800" />
                        )}
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Help callout */}
              <div className="mt-8 bg-[#00D4FF]/10 border border-[#00D4FF]/20 rounded-lg p-4 flex items-start gap-3">
                <AlertCircle className="w-5 h-5 text-[#00D4FF] shrink-0 mt-0.5" />
                <div>
                  <h4 className="font-medium mb-1 text-white">Need help installing?</h4>
                  <p className="text-sm text-gray-400">
                    Check out our{" "}
                    <Link href="/docs/installation" className="text-[#00D4FF] hover:underline">
                      detailed installation guide
                    </Link>{" "}
                    or{" "}
                    <a
                      href="mailto:support@splice.app"
                      className="text-[#00D4FF] hover:underline"
                    >
                      contact support
                    </a>
                    .
                  </p>
                </div>
              </div>
            </motion.div>
          </div>
        </section>

        {/* CTA */}
        <section className="py-16 bg-[#060b14]">
          <div className="container mx-auto px-4 text-center">
            <h2 className="text-2xl font-bold mb-4 text-white">
              Don&apos;t have a license yet?
            </h2>
            <p className="text-gray-400 mb-6">
              Start your 14-day free trial with full access to all features.
            </p>
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold btn-glow"
              asChild
            >
              <Link href="/pricing">
                View Pricing
                <ArrowRight className="w-4 h-4 ml-2" />
              </Link>
            </Button>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

"use client";

import {
  Accordion,
  AccordionContent,
  AccordionItem,
  AccordionTrigger,
} from "@/components/ui/accordion";
import { motion } from "framer-motion";

const faqs = [
  {
    question: "How does the 14-day free trial work?",
    answer:
      "You get full access to all features for 14 days without entering any payment information. After the trial, you can choose a plan that fits your needs or simply let it expire - no strings attached.",
  },
  {
    question: "Which versions of Premiere Pro are supported?",
    answer:
      "SPLICE supports both CEP (Premiere Pro 14.0 to 25.9) and UXP (Premiere Pro 25.6+) architectures. This means we work with virtually every modern version of Premiere Pro, and we're future-proof for Adobe's new extension system.",
  },
  {
    question: "How accurate is the AI silence detection?",
    answer:
      "Our advanced silence detection achieves 95%+ accuracy using multiple analysis methods. You can fine-tune sensitivity settings to match your content style, whether it's a fast-paced vlog or a slower tutorial.",
  },
  {
    question: "Can I use the AI-generated music commercially?",
    answer:
      "Yes! All music generated through SPLICE is royalty-free and cleared for commercial use, including YouTube monetization, client work, and social media content.",
  },
  {
    question: "What happens if I exceed my monthly hours?",
    answer:
      "You can continue using SPLICE with overage billing at $1/hour. We'll notify you when you're approaching your limit so there are no surprises. You can also upgrade your plan at any time.",
  },
  {
    question: "How is SPLICE different from FireCut or AutoPod?",
    answer:
      "SPLICE offers features no competitor has: AI music generation, text-based editing (delete text = delete video), and advanced face-tracking social reframe. We also support modern UXP architecture while they're limited to legacy CEP only.",
  },
  {
    question: "Is my video data secure?",
    answer:
      "Your video files are processed securely and never stored permanently on our servers. Audio is extracted temporarily for transcription, then deleted within 24 hours. We're GDPR compliant and never train AI models on your content.",
  },
  {
    question: "Can I cancel anytime?",
    answer:
      "Absolutely. You can cancel your subscription at any time from your account settings. You'll continue to have access until the end of your billing period, and we don't charge cancellation fees.",
  },
];

export function FAQ() {
  return (
    <section className="py-24">
      <div className="container mx-auto px-4">
        {/* Section header */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Frequently Asked{" "}
            <span className="gradient-text">Questions</span>
          </h2>
          <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
            Everything you need to know about SPLICE. Can&apos;t find what you&apos;re
            looking for? Contact our support team.
          </p>
        </motion.div>

        {/* FAQ accordion */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="max-w-3xl mx-auto"
        >
          <Accordion type="single" collapsible className="w-full">
            {faqs.map((faq, index) => (
              <AccordionItem key={index} value={`item-${index}`}>
                <AccordionTrigger className="text-left hover:no-underline">
                  {faq.question}
                </AccordionTrigger>
                <AccordionContent className="text-muted-foreground">
                  {faq.answer}
                </AccordionContent>
              </AccordionItem>
            ))}
          </Accordion>
        </motion.div>
      </div>
    </section>
  );
}

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-all disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg:not([class*='size-'])]:size-4 shrink-0 [&_svg]:shrink-0 outline-none focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:ring-[3px] aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-white hover:bg-destructive/90 focus-visible:ring-destructive/20 dark:focus-visible:ring-destructive/40 dark:bg-destructive/60",
        outline:
          "border bg-background shadow-xs hover:bg-accent hover:text-accent-foreground dark:bg-input/30 dark:border-input dark:hover:bg-input/50",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost:
          "hover:bg-accent hover:text-accent-foreground dark:hover:bg-accent/50",
        link: "text-primary underline-offset-4 hover:underline",
      },
      size: {
        default: "h-9 px-4 py-2 has-[>svg]:px-3",
        sm: "h-8 rounded-md gap-1.5 px-3 has-[>svg]:px-2.5",
        lg: "h-10 rounded-md px-6 has-[>svg]:px-4",
        icon: "size-9",
        "icon-sm": "size-8",
        "icon-lg": "size-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

function Button({
  className,
  variant = "default",
  size = "default",
  asChild = false,
  ...props
}: React.ComponentProps<"button"> &
  VariantProps<typeof buttonVariants> & {
    asChild?: boolean
  }) {
  const Comp = asChild ? Slot : "button"

  return (
    <Comp
      data-slot="button"
      data-variant={variant}
      data-size={size}
      className={cn(buttonVariants({ variant, size, className }))}
      {...props}
    />
  )
}

export { Button, buttonVariants }

