   221→
   222→      // Step 1: Export audio
   223→      const exportSuccess = await exportAudioInternal();
   224→      if (!exportSuccess) {
   225→        throw new Error('Audio export failed');
   226→      }
   227→
   228→      // Step 2: Get settings
   229→      const sensitivity = parseInt(ui.sensitivitySlider?.value || 50);
   230→      const useIsolated = ui.sourceIsolated?.checked ?? false;
   231→      const detectTakes = ui.enableTakesDetection?.checked ?? true;
   232→
   233→      // Step 3: Vocal isolation if needed
   234→      let audioPath = WAV_PATH;
   235→      if (useIsolated) {
   236→        // Check isolation access before proceeding
   237→        const credits = getCurrentCredits();
   238→        if (!credits || !credits.hasIsolationAccess) {
   239→          throw new Error('Vocal isolation requires Pro or Team tier. Upgrade to access this feature.');
   240→        }
   241→
   242→        // Show overage warning if applicable
   243→        const isolationMins = credits.isolationHoursRemaining * 60;
   244→        if (isolationMins <= 0) {
   245→          const overageRate = credits.isolationOverageRate || 0.08;
   246→          showProgress(`Isolating vocals (overage: $${overageRate}/min)...`);
   247→        } else {
   248→          showProgress(`Isolating vocals (${isolationMins.toFixed(0)} min remaining)...`);
   249→        }
   250→
   251→        const isolateResult = await isolateVocals();
   252→        if (isolateResult.success) {
   253→          audioPath = isolateResult.outputPath;
   254→          // Update credits display after isolation
   255→          await refreshCredits();
   256→        } else {
   257→          throw new Error(isolateResult.error || 'Vocal isolation failed');
   258→        }
   259→      }
   260→
   261→      // Step 4: Run detection (parallel if takes enabled)
   262→      const params = mapSensitivity(sensitivity);
   263→      let silences = [];
   264→      let takes = [];
   265→
   266→      if (detectTakes) {
   267→        // Parallel detection for performance
   268→        showProgress('Detecting silences and analyzing takes...');
   269→        const [silenceResult, transcriptResult] = await Promise.all([
   270→          detectSilences(audioPath, params),
   271→          transcribeAudio()
   272→        ]);
   273→
   274→        silences = silenceResult;
   275→
   276→        // Extract takes from transcript result
   277→        if (transcriptResult.success && transcriptResult.takes) {
   278→          if (transcriptResult.takes.takes) {
   279→            takes = transcriptResult.takes.takes;
   280→          } else if (Array.isArray(transcriptResult.takes)) {
   281→            takes = transcriptResult.takes;
   282→          }
   283→        }
   284→
   285→        // Store transcript for reference
   286→        window.currentTranscript = transcriptResult.transcript;
   287→      } else {
   288→        // Just detect silences
   289→        showProgress('Detecting silences...');
   290→        silences = await detectSilences(audioPath, params);
   291→      }
   292→
   293→      if (silences.length === 0) {
   294→        showEmptyState();
   295→        setStatus('No silences detected. Try increasing sensitivity.');
   296→        return;
   297→      }
   298→
   299→      // Step 5: Filter silences by takes (protect speech)
   300→      const filtered = filterSilencesByTakes(silences, takes);
   301→
   302→      // Step 6: Add timeline markers (clear old ones first)
   303→      showProgress('Adding timeline markers...');
   304→      await clearSpliceMarkers();
   305→      const markerResult = await addTimelineMarkers(filtered.safe, filtered.protected, takes);
   306→
   307→      // Step 7: Show combined preview
   308→      showCombinedPreview(filtered.safe, filtered.protected, takes);
   309→
   310→      const markerInfo = markerResult.added > 0 ? ` (${markerResult.added} markers added)` : '';
   311→      const statusMsg = takes.length > 0
   312→        ? `Found ${silences.length} silences, ${filtered.protected.length} protected by ${takes.length} takes${markerInfo}`
   313→        : `Found ${silences.length} silence(s) - review and apply${markerInfo}`;
   314→      setStatus(statusMsg);
   315→
   316→    } catch (err) {
   317→      showEmptyState();
   318→      setStatus('Error: ' + err.message);
   319→      console.error('[SPLICE] Unified workflow error:', err);
   320→    } finally {
   321→      isOperationInProgress = false;
   322→      ui.goBtn.disabled = false;
   323→    }
   324→  });
   325→}
