   545→/**
   546→ * Remove all SPLICE markers from the timeline.
   547→ * Call this before adding new markers to avoid duplicates.
   548→ */
   549→async function clearSpliceMarkers() {
   550→  try {
   551→    const context = await getActiveSequence();
   552→    if (!context) return 0;
   553→
   554→    const { sequence, project } = context;
   555→    const markers = await ppro.Markers.getMarkers(sequence);
   556→    if (!markers) return 0;
   557→
   558→    // Get all markers
   559→    const allMarkers = await markers.getMarkers();
   560→    if (!allMarkers || allMarkers.length === 0) return 0;
   561→
   562→    // Filter SPLICE markers
   563→    const spliceMarkers = [];
   564→    for (const marker of allMarkers) {
   565→      const name = await marker.getName();
   566→      if (name && name.startsWith('SPLICE:')) {
   567→        spliceMarkers.push(marker);
   568→      }
   569→    }
   570→
   571→    if (spliceMarkers.length === 0) return 0;
   572→
   573→    // Remove all SPLICE markers in a single transaction
   574→    await project.lockedAccess(async () => {
   575→      await project.executeTransaction((compoundAction) => {
   576→        for (const marker of spliceMarkers) {
   577→          const removeAction = markers.createRemoveMarkerAction(marker);
   578→          compoundAction.addAction(removeAction);
   579→        }
   580→      }, 'SPLICE: Clear Markers');
   581→    });
   582→
   583→    console.log(`[SPLICE] Cleared ${spliceMarkers.length} previous markers`);
   584→    return spliceMarkers.length;
   585→
   586→  } catch (err) {
   587→    console.error('[SPLICE] Error clearing markers:', err);
   588→    return 0;
   589→  }
   590→}
